00759                     
00760                         TMP_EditorUtility.DrawColorProperty(rect, topRight);
00761                         
00762                         bottomLeft.colorValue = topLeft.colorValue;
00763                         bottomRight.colorValue = topRight.colorValue;
00764                         break;
00765                     case ColorMode.VerticalGradient:
00766                         TMP_EditorUtility.DrawColorProperty(rect, topLeft);
00767 
00768                         rect = EditorGUILayout.GetControlRect(false, EditorGUIUtility.singleLineHeight * (EditorGUIUtility.wideMode ? 1 : 2));
00769                         rect.x += EditorGUIUtility.labelWidth;
00770                 
00771                         TMP_EditorUtility.DrawColorProperty(rect, bottomLeft);
00772                         
00773                         topRight.colorValue = topLeft.colorValue;
00774                         bottomRight.colorValue = bottomLeft.colorValue;
00775                         break;
00776                     case ColorMode.FourCornersGradient:
00777                         rect.width /= 2f;
00778 
00779                         TMP_EditorUtility.DrawColorProperty(rect, topLeft);
00780 
00781                         rect.x += rect.width;
