00872         void DrawWrappingOverflow()
00873         {
00874             // TEXT WRAPPING
00875             EditorGUI.BeginChangeCheck();
00876             int wrapSelection = EditorGUILayout.Popup(k_WrappingLabel, m_EnableWordWrappingProp.boolValue ? 1 : 0, k_WrappingOptions);
00877             if (EditorGUI.EndChangeCheck())
