00879                 m_EnableWordWrappingProp.boolValue = wrapSelection == 1;
00880                 m_HavePropertiesChanged = true;
00881                 m_IsInputPasingRequiredProp.boolValue = true;
00882             }
00883             
00884             // TEXT OVERFLOW
00885             EditorGUI.BeginChangeCheck();
00886 
00887             // Cache Reference to Linked Text Component
00888             TMP_Text oldLinkedComponent = m_LinkedTextComponentProp.objectReferenceValue as TMP_Text;
00889 
00890             if ((TextOverflowModes)m_TextOverflowModeProp.enumValueIndex == TextOverflowModes.Linked)
00891             {
00892                 EditorGUILayout.BeginHorizontal();
00893                 EditorGUILayout.PropertyField(m_TextOverflowModeProp, k_OverflowLabel);
00894 
00895                 EditorGUILayout.PropertyField(m_LinkedTextComponentProp, GUIContent.none);
00896                 
00897                 EditorGUILayout.EndHorizontal();
00898 
00899                 if (GUI.changed)
00900                 {
00901                     TMP_Text linkedComponent = m_LinkedTextComponentProp.objectReferenceValue as TMP_Text;
00902 
00903                     if (linkedComponent)
00904                         m_TextComponent.linkedTextComponent = linkedComponent;
00905 
00906                 }
00907             }
00908             else if ((TextOverflowModes)m_TextOverflowModeProp.enumValueIndex == TextOverflowModes.Page)
00909             {
00910                 EditorGUILayout.BeginHorizontal();
00911                 EditorGUILayout.PropertyField(m_TextOverflowModeProp, k_OverflowLabel);
00912                 EditorGUILayout.PropertyField(m_PageToDisplayProp, GUIContent.none);
00913                 EditorGUILayout.EndHorizontal();
00914 
00915                 if (oldLinkedComponent)
00916                     m_TextComponent.linkedTextComponent = null;
00917             }
00918             else
00919             {
00920                 EditorGUILayout.PropertyField(m_TextOverflowModeProp, k_OverflowLabel);
00921 
00922                 if (oldLinkedComponent)
00923                     m_TextComponent.linkedTextComponent = null;
00924             }
00925 
00926             if (EditorGUI.EndChangeCheck())
00927             {
00928                 m_HavePropertiesChanged = true;
00929                 m_IsInputPasingRequiredProp.boolValue = true;
00930             }
00931 
00932             EditorGUILayout.Space();
00933         }
