01037         protected GUIContent[] GetMaterialPresets()
01038         {
01039             TMP_FontAsset fontAsset = m_FontAssetProp.objectReferenceValue as TMP_FontAsset;
01040             if (fontAsset == null) return null;
01041 
01042             m_MaterialPresets = TMP_EditorUtility.FindMaterialReferences(fontAsset);
01043             m_MaterialPresetNames = new GUIContent[m_MaterialPresets.Length];
01044 
01045             for (int i = 0; i < m_MaterialPresetNames.Length; i++)
01046             {
01047                 m_MaterialPresetNames[i] = new GUIContent(m_MaterialPresets[i].name);
01048 
01049                 if (m_TargetMaterial.GetInstanceID() == m_MaterialPresets[i].GetInstanceID())
01050                     m_MaterialPresetSelectionIndex = i;
01051             }
01052 
01053             m_IsPresetListDirty = false;
01054 
01055             return m_MaterialPresetNames;
01056         }
