01084             pos0.x += fieldWidth;
01085             GUI.Label(pos0, k_RightLabel);
01086 
01087             pos0.x += fieldWidth;
01088             GUI.Label(pos0, k_BottomLabel);
01089 
01090             pos0.y += 18;
01091 
01092             pos0.x = EditorGUIUtility.labelWidth;
01093             vec.x = EditorGUI.FloatField(pos0, GUIContent.none, vec.x);
01094 
01095             pos0.x += fieldWidth;
01096             vec.y = EditorGUI.FloatField(pos0, GUIContent.none, vec.y);
01097 
