00205         public void RefreshShownValue()
00206         {
00207             OptionData data = s_NoOptionData;
00208 
00209             if (options.Count > 0)
00210                 data = options[Mathf.Clamp(m_Value, 0, options.Count - 1)];
00211 
00212             if (m_CaptionText)
00213             {
00214                 if (data != null && data.text != null)
00215                     m_CaptionText.text = data.text;
00216                 else
00217                     m_CaptionText.text = "";
00218             }
00219 
00220             if (m_CaptionImage)
00221             {
00222                 if (data != null)
00223                     m_CaptionImage.sprite = data.image;
00224                 else
00225                     m_CaptionImage.sprite = null;
00226                 m_CaptionImage.enabled = (m_CaptionImage.sprite != null);
00227             }
00228         }
