00256         private void SetupTemplate()
00257         {
00258             validTemplate = false;
00259 
00260             if (!m_Template)
00261             {
00262                 Debug.LogError("The dropdown template is not assigned. The template needs to be assigned and must have a child GameObject with a Toggle component serving as the item.", this);
00263                 return;
00264             }
00265 
00266             GameObject templateGo = m_Template.gameObject;
00267             templateGo.SetActive(true);
00268             Toggle itemToggle = m_Template.GetComponentInChildren<Toggle>();
00269 
00270             validTemplate = true;
00271             if (!itemToggle || itemToggle.transform == template)
00272             {
00273                 validTemplate = false;
00274                 Debug.LogError("The dropdown template is not valid. The template must have a child GameObject with a Toggle component serving as the item.", template);
00275             }
00276             else if (!(itemToggle.transform.parent is RectTransform))
00277             {
00278                 validTemplate = false;
00279                 Debug.LogError("The dropdown template is not valid. The child GameObject with a Toggle component (the item) must have a RectTransform on its parent.", template);
00280             }
00281             else if (itemText != null && !itemText.transform.IsChildOf(itemToggle.transform))
00282             {
00283                 validTemplate = false;
00284                 Debug.LogError("The dropdown template is not valid. The Item Text must be on the item GameObject or children of it.", template);
00285             }
00286             else if (itemImage != null && !itemImage.transform.IsChildOf(itemToggle.transform))
00287             {
00288                 validTemplate = false;
00289                 Debug.LogError("The dropdown template is not valid. The Item Image must be on the item GameObject or children of it.", template);
00290             }
00291 
00292             if (!validTemplate)
00293             {
00294                 templateGo.SetActive(false);
00295                 return;
00296             }
00297 
00298             DropdownItem item = itemToggle.gameObject.AddComponent<DropdownItem>();
00299             item.text = m_ItemText;
00300             item.image = m_ItemImage;
00301             item.toggle = itemToggle;
00302             item.rectTransform = (RectTransform)itemToggle.transform;
00303 
00304             Canvas popupCanvas = GetOrAddComponent<Canvas>(templateGo);
00305             popupCanvas.overrideSorting = true;
00306             popupCanvas.sortingOrder = 30000;
00307 
00308             GetOrAddComponent<GraphicRaycaster>(templateGo);
00309             GetOrAddComponent<CanvasGroup>(templateGo);
00310             templateGo.SetActive(false);
00311 
00312             validTemplate = true;
00313         }
00314 
00315         private static T GetOrAddComponent<T>(GameObject go) where T : Component
00316         {
00317             T comp = go.GetComponent<T>();
00318             if (!comp)
00319                 comp = go.AddComponent<T>();
00320             return comp;
00321         }
