00338         // Show the dropdown.
00339         //
00340         // Plan for dropdown scrolling to ensure dropdown is contained within screen.
00341         //
00342         // We assume the Canvas is the screen that the dropdown must be kept inside.
00343         // This is always valid for screen space canvas modes.
00344         // For world space canvases we don't know how it's used, but it could be e.g. for an in-game monitor.
00345         // We consider it a fair constraint that the canvas must be big enough to contains dropdowns.
00346         public void Show()
00347         {
00348             if (!IsActive() || !IsInteractable() || m_Dropdown != null)
00349                 return;
00350 
00351             if (!validTemplate)
00352             {
00353                 SetupTemplate();
00354                 if (!validTemplate)
00355                     return;
00356             }
00357 
00358             // Get root Canvas.
00359             var list = TMP_ListPool<Canvas>.Get();
00360             gameObject.GetComponentsInParent(false, list);
00361             if (list.Count == 0)
00362                 return;
00363             Canvas rootCanvas = list[0];
00364             TMP_ListPool<Canvas>.Release(list);
00365 
00366             m_Template.gameObject.SetActive(true);
00367 
00368             // Instantiate the drop-down template
00369             m_Dropdown = CreateDropdownList(m_Template.gameObject);
00370             m_Dropdown.name = "Dropdown List";
00371             m_Dropdown.SetActive(true);
00372 
00373             // Make drop-down RectTransform have same values as original.
00374             RectTransform dropdownRectTransform = m_Dropdown.transform as RectTransform;
00375             dropdownRectTransform.SetParent(m_Template.transform.parent, false);
00376 
00377             // Instantiate the drop-down list items
00378 
00379             // Find the dropdown item and disable it.
00380             DropdownItem itemTemplate = m_Dropdown.GetComponentInChildren<DropdownItem>();
00381 
00382             GameObject content = itemTemplate.rectTransform.parent.gameObject;
00383             RectTransform contentRectTransform = content.transform as RectTransform;
00384             itemTemplate.rectTransform.gameObject.SetActive(true);
00385 
00386             // Get the rects of the dropdown and item
00387             Rect dropdownContentRect = contentRectTransform.rect;
00388             Rect itemTemplateRect = itemTemplate.rectTransform.rect;
00389 
00390             // Calculate the visual offset between the item's edges and the background's edges
00391             Vector2 offsetMin = itemTemplateRect.min - dropdownContentRect.min + (Vector2)itemTemplate.rectTransform.localPosition;
00392             Vector2 offsetMax = itemTemplateRect.max - dropdownContentRect.max + (Vector2)itemTemplate.rectTransform.localPosition;
00393             Vector2 itemSize = itemTemplateRect.size;
00394 
00395             m_Items.Clear();
00396 
00397             Toggle prev = null;
00398             for (int i = 0; i < options.Count; ++i)
00399             {
00400                 OptionData data = options[i];
00401                 DropdownItem item = AddItem(data, value == i, itemTemplate, m_Items);
00402                 if (item == null)
00403                     continue;
00404 
00405                 // Automatically set up a toggle state change listener
00406                 item.toggle.isOn = value == i;
00407                 item.toggle.onValueChanged.AddListener(x => OnSelectItem(item.toggle));
00408 
00409                 // Select current option
00410                 if (item.toggle.isOn)
00411                     item.toggle.Select();
00412 
00413                 // Automatically set up explicit navigation
00414                 if (prev != null)
00415                 {
00416                     Navigation prevNav = prev.navigation;
00417                     Navigation toggleNav = item.toggle.navigation;
00418                     prevNav.mode = Navigation.Mode.Explicit;
00419                     toggleNav.mode = Navigation.Mode.Explicit;
00420 
00421                     prevNav.selectOnDown = item.toggle;
00422                     prevNav.selectOnRight = item.toggle;
00423                     toggleNav.selectOnLeft = prev;
00424                     toggleNav.selectOnUp = prev;
00425 
00426                     prev.navigation = prevNav;
00427                     item.toggle.navigation = toggleNav;
00428                 }
00429                 prev = item.toggle;
00430             }
00431 
00432             // Reposition all items now that all of them have been added
00433             Vector2 sizeDelta = contentRectTransform.sizeDelta;
00434             sizeDelta.y = itemSize.y * m_Items.Count + offsetMin.y - offsetMax.y;
00435             contentRectTransform.sizeDelta = sizeDelta;
00436 
00437             float extraSpace = dropdownRectTransform.rect.height - contentRectTransform.rect.height;
00438             if (extraSpace > 0)
00439                 dropdownRectTransform.sizeDelta = new Vector2(dropdownRectTransform.sizeDelta.x, dropdownRectTransform.sizeDelta.y - extraSpace);
00440 
00441             // Invert anchoring and position if dropdown is partially or fully outside of canvas rect.
00442             // Typically this will have the effect of placing the dropdown above the button instead of below,
00443             // but it works as inversion regardless of initial setup.
00444             Vector3[] corners = new Vector3[4];
00445             dropdownRectTransform.GetWorldCorners(corners);
00446 
00447             RectTransform rootCanvasRectTransform = rootCanvas.transform as RectTransform;
00448             Rect rootCanvasRect = rootCanvasRectTransform.rect;
00449             for (int axis = 0; axis < 2; axis++)
00450             {
00451             bool outside = false;
00452             for (int i = 0; i < 4; i++)
00453             {
00454                 Vector3 corner = rootCanvasRectTransform.InverseTransformPoint(corners[i]);
00455                     if (corner[axis] < rootCanvasRect.min[axis] || corner[axis] > rootCanvasRect.max[axis])
00456                 {
00457                     outside = true;
00458                     break;
00459                 }
00460             }
00461             if (outside)
00462                     RectTransformUtility.FlipLayoutOnAxis(dropdownRectTransform, axis, false, false);
00463             }
00464 
00465             for (int i = 0; i < m_Items.Count; i++)
00466             {
00467                 RectTransform itemRect = m_Items[i].rectTransform;
00468                 itemRect.anchorMin = new Vector2(itemRect.anchorMin.x, 0);
00469                 itemRect.anchorMax = new Vector2(itemRect.anchorMax.x, 0);
00470                 itemRect.anchoredPosition = new Vector2(itemRect.anchoredPosition.x, offsetMin.y + itemSize.y * (m_Items.Count - 1 - i) + itemSize.y * itemRect.pivot.y);
00471                 itemRect.sizeDelta = new Vector2(itemRect.sizeDelta.x, itemSize.y);
00472             }
00473 
00474             // Fade in the popup
00475             AlphaFadeList(0.15f, 0f, 1f);
00476 
00477             // Make drop-down template and item template inactive
00478             m_Template.gameObject.SetActive(false);
00479             itemTemplate.gameObject.SetActive(false);
00480 
00481             m_Blocker = CreateBlocker(rootCanvas);
00482         }
00483 
00484         protected virtual GameObject CreateBlocker(Canvas rootCanvas)
00485         {
00486             // Create blocker GameObject.
00487             GameObject blocker = new GameObject("Blocker");
00488 
00489             // Setup blocker RectTransform to cover entire root canvas area.
00490             RectTransform blockerRect = blocker.AddComponent<RectTransform>();
00491             blockerRect.SetParent(rootCanvas.transform, false);
00492             blockerRect.anchorMin = Vector3.zero;
00493             blockerRect.anchorMax = Vector3.one;
00494             blockerRect.sizeDelta = Vector2.zero;
00495 
00496             // Make blocker be in separate canvas in same layer as dropdown and in layer just below it.
00497             Canvas blockerCanvas = blocker.AddComponent<Canvas>();
00498             blockerCanvas.overrideSorting = true;
00499             Canvas dropdownCanvas = m_Dropdown.GetComponent<Canvas>();
00500             blockerCanvas.sortingLayerID = dropdownCanvas.sortingLayerID;
00501             blockerCanvas.sortingOrder = dropdownCanvas.sortingOrder - 1;
00502 
00503             // Add raycaster since it's needed to block.
00504             blocker.AddComponent<GraphicRaycaster>();
00505 
00506             // Add image since it's needed to block, but make it clear.
00507             Image blockerImage = blocker.AddComponent<Image>();
00508             blockerImage.color = Color.clear;
00509 
00510             // Add button since it's needed to block, and to close the dropdown when blocking area is clicked.
00511             Button blockerButton = blocker.AddComponent<Button>();
00512             blockerButton.onClick.AddListener(Hide);
00513 
00514             return blocker;
00515         }
