00542         // Add a new drop-down list item with the specified values.
00543         private DropdownItem AddItem(OptionData data, bool selected, DropdownItem itemTemplate, List<DropdownItem> items)
00544         {
00545             // Add a new item to the dropdown.
00546             DropdownItem item = CreateItem(itemTemplate);
00547             item.rectTransform.SetParent(itemTemplate.rectTransform.parent, false);
00548 
00549             item.gameObject.SetActive(true);
00550             item.gameObject.name = "Item " + items.Count + (data.text != null ? ": " + data.text : "");
00551 
00552             if (item.toggle != null)
00553             {
00554                 item.toggle.isOn = false;
00555             }
00556 
00557             // Set the item's data
00558             if (item.text)
00559                 item.text.text = data.text;
00560             if (item.image)
00561             {
00562                 item.image.sprite = data.image;
00563                 item.image.enabled = (item.image.sprite != null);
00564             }
00565 
00566             items.Add(item);
00567             return item;
00568         }
