00570         private void AlphaFadeList(float duration, float alpha)
00571         {
00572             CanvasGroup group = m_Dropdown.GetComponent<CanvasGroup>();
00573             AlphaFadeList(duration, group.alpha, alpha);
00574         }
00575 
00576         private void AlphaFadeList(float duration, float start, float end)
00577         {
00578             if (end.Equals(start))
00579                 return;
00580 
00581             FloatTween tween = new FloatTween { duration = duration, startValue = start, targetValue = end };
00582             tween.AddOnChangedCallback(SetAlpha);
00583             tween.ignoreTimeScale = true;
00584             m_AlphaTweenRunner.StartTween(tween);
00585         }
00586 
00587         private void SetAlpha(float alpha)
00588         {
00589             if (!m_Dropdown)
00590                 return;
00591             CanvasGroup group = m_Dropdown.GetComponent<CanvasGroup>();
00592             group.alpha = alpha;
00593         }
00594 
00595         // Hide the dropdown.
00596         public void Hide()
00597         {
00598             if (m_Dropdown != null)
00599             {
00600                 AlphaFadeList(0.15f, 0f);
00601 
00602                 // User could have disabled the dropdown during the OnValueChanged call.
00603                 if (IsActive())
00604                 StartCoroutine(DelayedDestroyDropdownList(0.15f));
00605             }
00606             if (m_Blocker != null)
00607                 DestroyBlocker(m_Blocker);
00608             m_Blocker = null;
00609             Select();
00610         }
00611 
00612         private IEnumerator DelayedDestroyDropdownList(float delay)
00613         {
00614 
00615                 yield return new WaitForSecondsRealtime(delay);
00616 
00617             for (int i = 0; i < m_Items.Count; i++)
00618             {
00619                 if (m_Items[i] != null)
00620                     DestroyItem(m_Items[i]);
00621                 m_Items.Clear();
00622             }
00623             if (m_Dropdown != null)
00624                 DestroyDropdownList(m_Dropdown);
00625             m_Dropdown = null;
00626         }
00627 
00628         // Change the value and hide the dropdown.
00629         private void OnSelectItem(Toggle toggle)
00630         {
00631             if (!toggle.isOn)
00632                 toggle.isOn = true;
00633 
00634             int selectedIndex = -1;
00635             Transform tr = toggle.transform;
00636             Transform parent = tr.parent;
00637             for (int i = 0; i < parent.childCount; i++)
00638             {
00639                 if (parent.GetChild(i) == tr)
00640                 {
00641                     // Subtract one to account for template child.
00642                     selectedIndex = i - 1;
00643                     break;
00644                 }
00645             }
00646 
00647             if (selectedIndex < 0)
00648                 return;
00649 
00650             value = selectedIndex;
00651             Hide();
00652         }
00653     }
