00093         [Test]
00094         [TestCase(4, 3423, 453, 500, 1)]
00095         [TestCase(3, 2500, 343, 370, 1)]
00096         [TestCase(2, 1500, 228, 241, 1)]
00097         [TestCase(1, 104, 14, 15, 1)]
00098         [TestCase(0, 22, 4, 5, 1)]
00099         public void TextParsing_TextInfoTest_WordWrappingDisabled(int sourceTextIndex, int characterCount, int spaceCount, int wordCount, int lineCount)
00100         {
00101             m_TextComponent.text = testStrings[sourceTextIndex];
00102             m_TextComponent.enableWordWrapping = false;
00103             m_TextComponent.alignment = TextAlignmentOptions.TopLeft;
00104 
00105             // Size the RectTransform
00106             m_TextComponent.rectTransform.sizeDelta = new Vector2(50, 5);
00107 
00108             // Force text generation to populate the TextInfo data structure.
00109             m_TextComponent.ForceMeshUpdate();
00110 
00111             Assert.AreEqual(m_TextComponent.textInfo.characterCount, characterCount);
00112             Assert.AreEqual(m_TextComponent.textInfo.spaceCount, spaceCount);
00113             Assert.AreEqual(m_TextComponent.textInfo.wordCount, wordCount);
00114             Assert.AreEqual(m_TextComponent.textInfo.lineCount, lineCount);
00115         }
