00173         public void AddGlyphInfo(TMP_Glyph[] glyphInfo)
00174         {
00175             m_glyphInfoList = new List<TMP_Glyph>();
00176             int characterCount = glyphInfo.Length;
00177 
00178             m_fontInfo.CharacterCount = characterCount;
00179             m_characterSet = new int[characterCount];
00180 
00181             for (int i = 0; i < characterCount; i++)
00182             {
00183                 TMP_Glyph g = new TMP_Glyph();
00184                 g.id = glyphInfo[i].id;
00185                 g.x = glyphInfo[i].x;
00186                 g.y = glyphInfo[i].y;
00187                 g.width = glyphInfo[i].width;
00188                 g.height = glyphInfo[i].height;
00189                 g.xOffset = glyphInfo[i].xOffset;
00190                 g.yOffset = (glyphInfo[i].yOffset);
00191                 g.xAdvance = glyphInfo[i].xAdvance;
00192                 g.scale = 1;
00193 
00194                 m_glyphInfoList.Add(g);
00195 
00196                 // While iterating through list of glyphs, find the Descender & Ascender for this GlyphSet.
00197                 //m_fontInfo.Ascender = Mathf.Max(m_fontInfo.Ascender, glyphInfo[i].yOffset);
00198                 //m_fontInfo.Descender = Mathf.Min(m_fontInfo.Descender, glyphInfo[i].yOffset - glyphInfo[i].height);
00199                 //Debug.Log(m_fontInfo.Ascender + "  " + m_fontInfo.Descender);
00200                 m_characterSet[i] = g.id; // Add Character ID to Array to make it easier to get the kerning pairs.
00201             }
00202 
00203             // Sort List by ID.
00204             m_glyphInfoList = m_glyphInfoList.OrderBy(s => s.id).ToList();
00205         }
