00221         public void ReadFontDefinition()
00222         {
00223             //Debug.Log("Reading Font Definition for " + this.name + ".");
00224             // Make sure that we have a Font Asset file assigned.
00225             if (m_fontInfo == null)
00226             {
00227                 return;
00228             }
00229 
00230             // Check Font Asset type
00231             //Debug.Log(name + "   " + fontAssetType);
00232 
00233             // Create new instance of GlyphInfo Dictionary for fast access to glyph info.
00234             m_characterDictionary = new Dictionary<int, TMP_Glyph>();
00235             for (int i = 0; i < m_glyphInfoList.Count; i++)
00236             {
00237                 TMP_Glyph glyph = m_glyphInfoList[i];
00238 
00239                 if (!m_characterDictionary.ContainsKey(glyph.id))
00240                     m_characterDictionary.Add(glyph.id, glyph);
00241 
00242                 // Compatibility
00243                 if (glyph.scale == 0) glyph.scale = 1;
00244             }
00245 
00246 
00247             //Debug.Log("PRE: BaseLine:" + m_fontInfo.Baseline + "  Ascender:" + m_fontInfo.Ascender + "  Descender:" + m_fontInfo.Descender); // + "  Centerline:" + m_fontInfo.CenterLine);
00248 
00249             TMP_Glyph temp_charInfo = new TMP_Glyph();
00250 
00251             // Add Character (10) LineFeed, (13) Carriage Return & Space (32) to Dictionary if they don't exists.
00252             if (m_characterDictionary.ContainsKey(32))
00253             {
00254                 m_characterDictionary[32].width = m_characterDictionary[32].xAdvance; // m_fontInfo.Ascender / 5;
00255                 m_characterDictionary[32].height = m_fontInfo.Ascender - m_fontInfo.Descender;
00256                 m_characterDictionary[32].yOffset= m_fontInfo.Ascender;
00257                 m_characterDictionary[32].scale = 1;
00258             }
00259             else
00260             {
00261                 //Debug.Log("Adding Character 32 (Space) to Dictionary for Font (" + m_fontInfo.Name + ").");
00262                 temp_charInfo = new TMP_Glyph();
00263                 temp_charInfo.id = 32;
00264                 temp_charInfo.x = 0; 
00265                 temp_charInfo.y = 0;
00266                 temp_charInfo.width = m_fontInfo.Ascender / 5;
00267                 temp_charInfo.height = m_fontInfo.Ascender - m_fontInfo.Descender;
00268                 temp_charInfo.xOffset = 0; 
00269                 temp_charInfo.yOffset = m_fontInfo.Ascender; 
00270                 temp_charInfo.xAdvance = m_fontInfo.PointSize / 4;
00271                 temp_charInfo.scale = 1;
00272                 m_characterDictionary.Add(32, temp_charInfo);
00273             }
00274 
00275             // Add Non-Breaking Space (160)
00276             if (!m_characterDictionary.ContainsKey(160))
00277             {
00278                 temp_charInfo = TMP_Glyph.Clone(m_characterDictionary[32]);
00279                 m_characterDictionary.Add(160, temp_charInfo);
00280             }
00281 
00282             // Add Zero Width Space (8203)
00283             if (!m_characterDictionary.ContainsKey(8203))
00284             {
00285                 temp_charInfo = TMP_Glyph.Clone(m_characterDictionary[32]);
00286                 temp_charInfo.width = 0;
00287                 temp_charInfo.xAdvance = 0;
00288                 m_characterDictionary.Add(8203, temp_charInfo);
00289             }
00290 
00291             //Add Zero Width no-break space (8288)
00292             if (!m_characterDictionary.ContainsKey(8288))
00293             {
00294                 temp_charInfo = TMP_Glyph.Clone(m_characterDictionary[32]);
00295                 temp_charInfo.width = 0;
00296                 temp_charInfo.xAdvance = 0;
00297                 m_characterDictionary.Add(8288, temp_charInfo);
00298             }
00299 
00300             // Add Linefeed (10)
00301             if (m_characterDictionary.ContainsKey(10) == false)
00302             {
00303                 //Debug.Log("Adding Character 10 (Linefeed) to Dictionary for Font (" + m_fontInfo.Name + ").");
00304 
00305                 temp_charInfo = new TMP_Glyph();
00306                 temp_charInfo.id = 10;
00307                 temp_charInfo.x = 0; // m_characterDictionary[32].x;
00308                 temp_charInfo.y = 0; // m_characterDictionary[32].y;
00309                 temp_charInfo.width = 10; // m_characterDictionary[32].width;
00310                 temp_charInfo.height = m_characterDictionary[32].height;
00311                 temp_charInfo.xOffset = 0; // m_characterDictionary[32].xOffset;
00312                 temp_charInfo.yOffset = m_characterDictionary[32].yOffset;
00313                 temp_charInfo.xAdvance = 0;
00314                 temp_charInfo.scale = 1;
00315                 m_characterDictionary.Add(10, temp_charInfo);
00316 
00317                 if (!m_characterDictionary.ContainsKey(13))
00318                     m_characterDictionary.Add(13, temp_charInfo);
00319             }
00320 
00321             // Add Tab Character to Dictionary. Tab is Tab Size * Space Character Width.
00322             if (m_characterDictionary.ContainsKey(9) == false)
00323             {
00324                 //Debug.Log("Adding Character 9 (Tab) to Dictionary for Font (" + m_fontInfo.Name + ").");
00325 
00326                 temp_charInfo = new TMP_Glyph();
00327                 temp_charInfo.id = 9;
00328                 temp_charInfo.x = m_characterDictionary[32].x;
00329                 temp_charInfo.y = m_characterDictionary[32].y;
00330                 temp_charInfo.width = m_characterDictionary[32].width * tabSize + (m_characterDictionary[32].xAdvance - m_characterDictionary[32].width) * (tabSize - 1);
00331                 temp_charInfo.height = m_characterDictionary[32].height;
00332                 temp_charInfo.xOffset = m_characterDictionary[32].xOffset;
00333                 temp_charInfo.yOffset = m_characterDictionary[32].yOffset;
00334                 temp_charInfo.xAdvance = m_characterDictionary[32].xAdvance * tabSize;
00335                 temp_charInfo.scale = 1;
00336                 m_characterDictionary.Add(9, temp_charInfo);
00337             }
00338 
00339             // Centerline is located at the center of character like { or in the middle of the lowercase o.
00340             //m_fontInfo.CenterLine = m_characterDictionary[111].yOffset - m_characterDictionary[111].height * 0.5f;
00341 
00342             // Tab Width is using the same xAdvance as space (32).
00343             m_fontInfo.TabWidth = m_characterDictionary[9].xAdvance;
00344 
00345             // Set Cap Height
00346             if (m_fontInfo.CapHeight == 0 && m_characterDictionary.ContainsKey(72))
00347                 m_fontInfo.CapHeight = m_characterDictionary[72].yOffset;
00348 
00349             // Adjust Font Scale for compatibility reasons
00350             if (m_fontInfo.Scale == 0)
00351                 m_fontInfo.Scale = 1.0f;
00352 
00353             // Set Strikethrough Offset (if needed)
00354             if (m_fontInfo.strikethrough == 0)
00355                 m_fontInfo.strikethrough = m_fontInfo.CapHeight / 2.5f;
00356 
00357             // Set Padding value for legacy font assets.
00358             if (m_fontInfo.Padding == 0)
00359             {
00360                 if (material.HasProperty(ShaderUtilities.ID_GradientScale))
00361                     m_fontInfo.Padding = material.GetFloat(ShaderUtilities.ID_GradientScale) - 1;
00362             }
00363 
00364             // Populate Dictionary with Kerning Information
00365             m_kerningDictionary = new Dictionary<int, KerningPair>();
00366             List<KerningPair> pairs = m_kerningInfo.kerningPairs;
00367 
00368             //Debug.Log(m_fontInfo.Name + " has " + pairs.Count +  " Kerning Pairs.");
00369             for (int i = 0; i < pairs.Count; i++)
00370             {
00371                 KerningPair pair = pairs[i];
00372 
00373                 // Convert legacy kerning data
00374                 if (pair.xOffset != 0)
00375                     pairs[i].ConvertLegacyKerningData();
00376 
00377                 KerningPairKey uniqueKey = new KerningPairKey(pair.firstGlyph, pair.secondGlyph);
00378 
00379                 if (m_kerningDictionary.ContainsKey((int)uniqueKey.key) == false)
00380                 {
00381                     m_kerningDictionary.Add((int)uniqueKey.key, pair);
00382                 }
00383                 else
00384                 {
00385                     if (!TMP_Settings.warningsDisabled)
00386                         Debug.LogWarning("Kerning Key for [" + uniqueKey.ascii_Left + "] and [" + uniqueKey.ascii_Right + "] already exists.");
00387                 }
00388             }
00389 
00390 
00391             // Compute Hashcode for the font asset name
00392             hashCode = TMP_TextUtilities.GetSimpleHashCode(this.name);
00393 
00394             // Compute Hashcode for the material name
00395             materialHashCode = TMP_TextUtilities.GetSimpleHashCode(material.name);
00396 
00397             // Unload font atlas texture
00398             //ShaderUtilities.GetShaderPropertyIDs();
00399             //Resources.UnloadAsset(material.GetTexture(ShaderUtilities.ID_MainTex));
00400 
00401             // Initialize Font Weights if needed
00402             //InitializeFontWeights();
00403         }
