00458         public bool HasCharacter(char character, bool searchFallbacks)
00459         {
00460             // Read font asset definition if it hasn't already been done.
00461             if (m_characterDictionary == null)
00462             {
00463                 ReadFontDefinition();
00464 
00465                 if (m_characterDictionary == null)
00466                     return false;
00467             }
00468 
00469             // Check font asset
00470             if (m_characterDictionary.ContainsKey(character))
00471                 return true;
00472 
00473             if (searchFallbacks)
00474             {
00475                 // Check font asset fallbacks
00476                 if (fallbackFontAssets != null && fallbackFontAssets.Count > 0)
00477                 {
00478                     for (int i = 0; i < fallbackFontAssets.Count && fallbackFontAssets[i] != null; i++)
00479                     {
00480                         if (fallbackFontAssets[i].HasCharacter_Internal(character, searchFallbacks))
00481                             return true;
00482                     }
00483                 }
00484 
00485                 // Check general fallback font assets.
00486                 if (TMP_Settings.fallbackFontAssets != null && TMP_Settings.fallbackFontAssets.Count > 0)
00487                 {
00488                     for (int i = 0; i < TMP_Settings.fallbackFontAssets.Count && TMP_Settings.fallbackFontAssets[i] != null; i++)
00489                     {
00490                         if (TMP_Settings.fallbackFontAssets[i].characterDictionary == null)
00491                             TMP_Settings.fallbackFontAssets[i].ReadFontDefinition();
00492 
00493                         if (TMP_Settings.fallbackFontAssets[i].characterDictionary != null && TMP_Settings.fallbackFontAssets[i].HasCharacter_Internal(character, searchFallbacks))
00494                             return true;
00495                     }
00496                 }
00497 
00498                 // Check TMP Settings Default Font Asset
00499                 if (TMP_Settings.defaultFontAsset != null)
00500                 {
00501                     if (TMP_Settings.defaultFontAsset.characterDictionary == null)
00502                         TMP_Settings.defaultFontAsset.ReadFontDefinition();
00503 
00504                     if (TMP_Settings.defaultFontAsset.characterDictionary != null && TMP_Settings.defaultFontAsset.HasCharacter_Internal(character, searchFallbacks))
00505                         return true;
00506                 }
00507             }
00508 
00509             return false;
00510         }
