00520         bool HasCharacter_Internal(char character, bool searchFallbacks)
00521         {
00522             // Read font asset definition if it hasn't already been done.
00523             if (m_characterDictionary == null)
00524             {
00525                 ReadFontDefinition();
00526 
00527                 if (m_characterDictionary == null)
00528                     return false;
00529             }
00530 
00531             // Check font asset
00532             if (m_characterDictionary.ContainsKey(character))
00533                 return true;
00534 
00535             if (searchFallbacks)
00536             {
00537                 // Check Font Asset Fallback fonts.
00538                 if (fallbackFontAssets != null && fallbackFontAssets.Count > 0)
00539                 {
00540                     for (int i = 0; i < fallbackFontAssets.Count && fallbackFontAssets[i] != null; i++)
00541                     {
00542                         if (fallbackFontAssets[i].HasCharacter_Internal(character, searchFallbacks))
00543                             return true;
00544                     }
00545                 }
00546             }
00547 
00548             return false;
00549         }
