00557         public bool HasCharacters(string text, out List<char> missingCharacters)
00558         {
00559             if (m_characterDictionary == null)
00560             {
00561                 missingCharacters = null;
00562                 return false;
00563             }
00564 
00565             missingCharacters = new List<char>();
00566 
00567             for (int i = 0; i < text.Length; i++)
00568             {
00569                 if (!m_characterDictionary.ContainsKey(text[i]))
00570                     missingCharacters.Add(text[i]);
00571             }
00572 
00573             if (missingCharacters.Count == 0)
00574                 return true;
00575 
00576             return false;
00577         }
