00755 
00756         void DisplayPageNavigation(ref int currentPage, int arraySize, int itemsPerPage)
00757         {
00758             Rect pagePos = EditorGUILayout.GetControlRect(false, 20);
00759             pagePos.width /= 3;
00760 
00761             int shiftMultiplier = Event.current.shift ? 10 : 1; // Page + Shift goes 10 page forward
00762 
00763             // Previous Page
00764             GUI.enabled = currentPage > 0;
00765 
00766             if (GUI.Button(pagePos, "Previous Page"))
00767                 currentPage -= 1 * shiftMultiplier;
00768 
00769 
00770             // Page Counter
00771             GUI.enabled = true;
00772             pagePos.x += pagePos.width;
00773             int totalPages = (int)(arraySize / (float)itemsPerPage + 0.999f);
00774             GUI.Label(pagePos, "Page " + (currentPage + 1) + " / " + totalPages, TMP_UIStyleManager.centeredLabel);
00775 
00776             // Next Page
00777             pagePos.x += pagePos.width;
00778             GUI.enabled = itemsPerPage * (currentPage + 1) < arraySize;
00779 
00780             if (GUI.Button(pagePos, "Next Page"))
00781                 currentPage += 1 * shiftMultiplier;
00782 
00783             // Clamp page range
00784             currentPage = Mathf.Clamp(currentPage, 0, arraySize / itemsPerPage);
00785 
00786             GUI.enabled = true;
00787         }
