00795         bool AddNewGlyph(int srcIndex, int dstGlyphID)
00796         {
00797             // Make sure Destination Glyph ID doesn't already contain a Glyph
00798             if (m_fontAsset.characterDictionary.ContainsKey(dstGlyphID))
00799                 return false;
00800 
00801             // Add new element to glyph list.
00802             m_glyphInfoList_prop.arraySize += 1;
00803 
00804             // Get a reference to the source glyph.
00805             SerializedProperty sourceGlyph = m_glyphInfoList_prop.GetArrayElementAtIndex(srcIndex);
00806 
00807             int dstIndex = m_glyphInfoList_prop.arraySize - 1;
00808 
00809             // Get a reference to the target / destination glyph.
00810             SerializedProperty targetGlyph = m_glyphInfoList_prop.GetArrayElementAtIndex(dstIndex);
00811 
00812             CopySerializedProperty(sourceGlyph, ref targetGlyph);
00813 
00814             // Update the ID of the glyph
00815             targetGlyph.FindPropertyRelative("id").intValue = dstGlyphID;
00816 
00817             serializedObject.ApplyModifiedProperties();
00818 
00819             m_fontAsset.SortGlyphs();
00820 
00821             m_fontAsset.ReadFontDefinition();
00822 
00823             return true;
00824         }
00825 
00830         void RemoveGlyphFromList(int index)
00831         {
00832             if (index > m_glyphInfoList_prop.arraySize)
00833                 return;
00834 
00835             m_glyphInfoList_prop.DeleteArrayElementAtIndex(index);
00836 
00837             serializedObject.ApplyModifiedProperties();
00838 
00839             m_fontAsset.ReadFontDefinition();
00840         }
