00843         // Check if any of the Style elements were clicked on.
00844         private bool DoSelectionCheck(Rect selectionArea)
00845         {
00846             Event currentEvent = Event.current;
00847 
00848             switch (currentEvent.type)
00849             {
00850                 case EventType.MouseDown:
00851                     if (selectionArea.Contains(currentEvent.mousePosition) && currentEvent.button == 0)
00852                     {
00853                         currentEvent.Use();
00854                         return true;
00855                     }
00856 
00857                     break;
00858             }
00859 
00860             return false;
00861         }
