00900         void SearchGlyphTable (string searchPattern, ref List<int> searchResults)
00901         {
00902             if (searchResults == null) searchResults = new List<int>();
00903 
00904             searchResults.Clear();
00905 
00906             int arraySize = m_glyphInfoList_prop.arraySize;
00907 
00908             for (int i = 0; i < arraySize; i++)
00909             {
00910                 SerializedProperty sourceGlyph = m_glyphInfoList_prop.GetArrayElementAtIndex(i);
00911 
00912                 int id = sourceGlyph.FindPropertyRelative("id").intValue;
00913 
00914                 // Check for potential match against a character.
00915                 if (searchPattern.Length == 1 && id == searchPattern[0])
00916                     searchResults.Add(i);
00917 
00918                 // Check for potential match against decimal id
00919                 if (id.ToString().Contains(searchPattern))
00920                     searchResults.Add(i);
00921 
00922                 if (id.ToString("x").Contains(searchPattern))
00923                     searchResults.Add(i);
00924 
00925                 if (id.ToString("X").Contains(searchPattern))
00926                     searchResults.Add(i);
00927             }
00928         }
