00930         void SearchKerningTable(string searchPattern, ref List<int> searchResults)
00931         {
00932             if (searchResults == null) searchResults = new List<int>();
00933 
00934             searchResults.Clear();
00935 
00936             int arraySize = m_kerningPairs_prop.arraySize;
00937 
00938             for (int i = 0; i < arraySize; i++)
00939             {
00940                 SerializedProperty sourceGlyph = m_kerningPairs_prop.GetArrayElementAtIndex(i);
00941 
00942                 int firstGlyph = sourceGlyph.FindPropertyRelative("m_FirstGlyph").intValue;
00943                 int secondGlyph = sourceGlyph.FindPropertyRelative("m_SecondGlyph").intValue;
00944 
00945                 if (searchPattern.Length == 1)
00946                 {
00947                     if (firstGlyph == searchPattern[0])
00948                     {
00949                         searchResults.Add(i);
00950                         continue;
00951                     }
00952 
00953                     if (secondGlyph == searchPattern[0])
00954                     {
00955                         searchResults.Add(i);
00956                         continue;
00957                     }
00958                 }
00959 
00960                 if (searchPattern.Length == 2)
00961                 {
00962                     if (firstGlyph == searchPattern[0] && secondGlyph == searchPattern[1])
00963                     {
00964                         searchResults.Add(i);
00965                         continue;
00966                     }
00967                 }
00968 
00969                 if (firstGlyph.ToString().Contains(searchPattern))
00970                 {
00971                     searchResults.Add(i);
00972                     continue;
00973                 }
00974 
00975                 //if (firstGlyph.ToString("x").Contains(searchPattern))
00976                 //{
00977                 //    searchResults.Add(i);
00978                 //    continue;
00979                 //}
00980 
00981                 //if (firstGlyph.ToString("X").Contains(searchPattern))
00982                 //{
00983                 //    searchResults.Add(i);
00984                 //    continue;
00985                 //}
00986 
00987                 if (secondGlyph.ToString().Contains(searchPattern))
00988                 {
00989                     searchResults.Add(i);
00990                     continue;
00991                 }
00992 
00993                 //if (secondGlyph.ToString("x").Contains(searchPattern))
00994                 //{
00995                 //    searchResults.Add(i);
00996                 //    continue;
00997                 //}
00998 
00999                 //if (secondGlyph.ToString("X").Contains(searchPattern))
01000                 //{
01001                 //    searchResults.Add(i);
01002                 //    continue;
01003                 //}
01004             }
01005         }
