00592         protected void ClampStringPos(ref int pos)
00593         {
00594             if (pos < 0)
00595                 pos = 0;
00596             else if (pos > text.Length)
00597                 pos = text.Length;
00598         }
00599 
00600         protected void ClampCaretPos(ref int pos)
00601         {
00602             if (pos < 0)
00603                 pos = 0;
00604             else if (pos > m_TextComponent.textInfo.characterCount - 1)
00605                 pos = m_TextComponent.textInfo.characterCount - 1;
00606         }
