00727 #if UNITY_EDITOR
00728         // Remember: This is NOT related to text validation!
00729         // This is Unity's own OnValidate method which is invoked when changing values in the Inspector.
00730         protected override void OnValidate()
00731         {
00732             base.OnValidate();
00733             EnforceContentType();
00734 
00735             m_CharacterLimit = Math.Max(0, m_CharacterLimit);
00736 
00737             //This can be invoked before OnEnabled is called. So we shouldn't be accessing other objects, before OnEnable is called.
00738             if (!IsActive())
00739                 return;
00740 
00741             SetTextComponentRichTextMode();
00742 
00743             UpdateLabel();
00744             if (m_AllowInput)
00745                 SetCaretActive();
00746         }
00747 
00748 #endif // if UNITY_EDITOR
00749 
00750         protected override void OnEnable()
00751         {
00752             //Debug.Log("*** OnEnable() *** - " + this.name);
00753 
00754             base.OnEnable();
00755 
00756             if (m_Text == null)
00757                 m_Text = string.Empty;
00758 
00759             if (Application.isPlaying)
00760             {
00761                 if (m_CachedInputRenderer == null && m_TextComponent != null)
00762                 {
00763                     GameObject go = new GameObject(transform.name + " Input Caret", typeof(RectTransform));
00764 
00765                     // Add MaskableGraphic Component
00766                     TMP_SelectionCaret caret = go.AddComponent<TMP_SelectionCaret>();
00767                     caret.raycastTarget = false;
00768                     caret.color = Color.clear;
00769 
00770                     go.hideFlags = HideFlags.DontSave;
00771                     go.transform.SetParent(m_TextComponent.transform.parent);
00772                     go.transform.SetAsFirstSibling();
00773                     go.layer = gameObject.layer;
00774 
00775                     caretRectTrans = go.GetComponent<RectTransform>();
00776                     m_CachedInputRenderer = go.GetComponent<CanvasRenderer>();
00777                     m_CachedInputRenderer.SetMaterial(Graphic.defaultGraphicMaterial, Texture2D.whiteTexture);
00778 
00779                     // Needed as if any layout is present we want the caret to always be the same as the text area.
00780                     go.AddComponent<LayoutElement>().ignoreLayout = true;
00781 
00782                     AssignPositioningIfNeeded();
00783                 }
00784             }
00785 
00786             // If we have a cached renderer then we had OnDisable called so just restore the material.
00787             if (m_CachedInputRenderer != null)
00788                 m_CachedInputRenderer.SetMaterial(Graphic.defaultGraphicMaterial, Texture2D.whiteTexture);
00789 
00790             if (m_TextComponent != null)
00791             {
00792                 m_TextComponent.RegisterDirtyVerticesCallback(MarkGeometryAsDirty);
00793                 m_TextComponent.RegisterDirtyVerticesCallback(UpdateLabel);
00794                 //m_TextComponent.ignoreRectMaskCulling = multiLine;
00795 
00796                 m_DefaultTransformPosition = m_TextComponent.rectTransform.localPosition;
00797 
00798                 // Cache reference to Vertical Scrollbar RectTransform and add listener.
00799                 if (m_VerticalScrollbar != null)
00800                 {
00801                     m_TextComponent.ignoreRectMaskCulling = true;
00802                     m_VerticalScrollbar.onValueChanged.AddListener(OnScrollbarValueChange);
00803                 }
00804 
00805                 UpdateLabel();
00806             }
00807 
00808             // Subscribe to event fired when text object has been regenerated.
00809             TMPro_EventManager.TEXT_CHANGED_EVENT.Add(ON_TEXT_CHANGED);
00810         }
00811 
00812         protected override void OnDisable()
00813         {
00814             // the coroutine will be terminated, so this will ensure it restarts when we are next activated
00815             m_BlinkCoroutine = null;
00816 
00817             DeactivateInputField();
00818             if (m_TextComponent != null)
00819             {
00820                 m_TextComponent.UnregisterDirtyVerticesCallback(MarkGeometryAsDirty);
00821                 m_TextComponent.UnregisterDirtyVerticesCallback(UpdateLabel);
00822 
00823                 if (m_VerticalScrollbar != null)
00824                     m_VerticalScrollbar.onValueChanged.RemoveListener(OnScrollbarValueChange);
00825 
00826             }
00827             CanvasUpdateRegistry.UnRegisterCanvasElementForRebuild(this);
00828 
00829             // Clear needs to be called otherwise sync never happens as the object is disabled.
00830             if (m_CachedInputRenderer != null)
00831                 m_CachedInputRenderer.Clear();
00832 
00833             if (m_Mesh != null)
00834                 DestroyImmediate(m_Mesh);
00835             m_Mesh = null;
00836 
00837             // Unsubscribe to event triggered when text object has been regenerated
00838             TMPro_EventManager.TEXT_CHANGED_EVENT.Remove(ON_TEXT_CHANGED);
00839 
00840             base.OnDisable();
00841         }
