00848         private void ON_TEXT_CHANGED(UnityEngine.Object obj)
00849         {
00850             if (obj == m_TextComponent && Application.isPlaying)
00851             {
00852                 caretPositionInternal = GetCaretPositionFromStringIndex(stringPositionInternal);
00853                 caretSelectPositionInternal = GetCaretPositionFromStringIndex(stringSelectPositionInternal);
00854 
00855                 //Debug.Log("Updating Caret position - Caret Position: " + m_CaretPosition + "  Caret Select Position: " + m_CaretSelectPosition);
00856             }
00857         }
00858 
00859 
00860         IEnumerator CaretBlink()
00861         {
00862             // Always ensure caret is initially visible since it can otherwise be confusing for a moment.
00863             m_CaretVisible = true;
00864             yield return null;
00865 
00866             while (/*isFocused &&*/ m_CaretBlinkRate > 0)
00867             {
00868                 // the blink rate is expressed as a frequency
00869                 float blinkPeriod = 1f / m_CaretBlinkRate;
00870 
00871                 // the caret should be ON if we are in the first half of the blink period
00872                 bool blinkState = (Time.unscaledTime - m_BlinkStartTime) % blinkPeriod < blinkPeriod / 2;
00873                 if (m_CaretVisible != blinkState)
00874                 {
00875                     m_CaretVisible = blinkState;
00876                     if (!hasSelection)
00877                         MarkGeometryAsDirty();
00878                 }
00879 
00880                 // Then wait again.
00881                 yield return null;
00882             }
00883             m_BlinkCoroutine = null;
00884         }
00885 
00886         void SetCaretVisible()
00887         {
00888             if (!m_AllowInput)
00889                 return;
00890 
00891             m_CaretVisible = true;
00892             m_BlinkStartTime = Time.unscaledTime;
00893             SetCaretActive();
00894         }
00895 
00896         // SetCaretActive will not set the caret immediately visible - it will wait for the next time to blink.
00897         // However, it will handle things correctly if the blink speed changed from zero to non-zero or non-zero to zero.
00898         void SetCaretActive()
00899         {
00900             if (!m_AllowInput)
00901                 return;
00902 
00903             if (m_CaretBlinkRate > 0.0f)
00904             {
00905                 if (m_BlinkCoroutine == null)
00906                     m_BlinkCoroutine = StartCoroutine(CaretBlink());
00907             }
00908             else
00909             {
00910                 m_CaretVisible = true;
00911             }
00912         }
