00931         public void MoveTextEnd(bool shift)
00932         {
00933             if (m_isRichTextEditingAllowed)
00934             {
00935                 int position = text.Length;
00936 
00937                 if (shift)
00938                 {
00939                     stringSelectPositionInternal = position;
00940                 }
00941                 else
00942                 {
00943                     stringPositionInternal = position;
00944                     stringSelectPositionInternal = stringPositionInternal;
00945                 }
00946             }
00947             else
00948             {
00949                 int position = m_TextComponent.textInfo.characterCount - 1;
00950 
00951                 if (shift)
00952                 {
00953                     caretSelectPositionInternal = position;
00954                     stringSelectPositionInternal = GetStringIndexFromCaretPosition(position);
00955                 }
00956                 else
00957                 {
00958                     caretPositionInternal = caretSelectPositionInternal = position;
00959                     stringSelectPositionInternal = stringPositionInternal = GetStringIndexFromCaretPosition(position);
00960                 }
00961             }
00962 
00963             UpdateLabel();
00964         }
