01010         public void MoveToEndOfLine(bool shift, bool ctrl)
01011         {
01012             // Get the line the caret is currently located on.
01013             int currentLine = m_TextComponent.textInfo.characterInfo[caretPositionInternal].lineNumber;
01014 
01015             // Get the last character of the given line.
01016             int position = ctrl == true ? m_TextComponent.textInfo.characterCount - 1 : m_TextComponent.textInfo.lineInfo[currentLine].lastCharacterIndex;
01017 
01018             position = GetStringIndexFromCaretPosition(position);
01019 
01020             if (shift)
01021             {
01022                 stringSelectPositionInternal = position;
01023             }
01024             else
01025             {
01026                 stringPositionInternal = position;
01027                 stringSelectPositionInternal = stringPositionInternal;
01028             }
01029 
01030             UpdateLabel();
01031         }
01032 
01037         public void MoveToStartOfLine(bool shift, bool ctrl)
01038         {
