01040             int currentLine = m_TextComponent.textInfo.characterInfo[caretPositionInternal].lineNumber;
01041 
01042             // Get the last character of the given line.
01043             int position = ctrl == true ? 0 : m_TextComponent.textInfo.lineInfo[currentLine].firstCharacterIndex;
01044 
01045             position = GetStringIndexFromCaretPosition(position);
01046 
01047             if (shift)
01048             {
01049                 stringSelectPositionInternal = position;
01050             }
01051             else
01052             {
01053                 stringPositionInternal = position;
01054                 stringSelectPositionInternal = stringPositionInternal;
01055             }
01056 
01057             UpdateLabel();
01058         }
