01081         // TODO: Make LateUpdate a coroutine instead. Allows us to control the update to only be when the field is active.
01082         protected virtual void LateUpdate()
01083         {
01084             // Only activate if we are not already activated.
01085             if (m_ShouldActivateNextUpdate)
01086             {
01087                 if (!isFocused)
01088                 {
01089                     ActivateInputFieldInternal();
01090                     m_ShouldActivateNextUpdate = false;
01091                     return;
01092                 }
01093 
01094                 // Reset as we are already activated.
01095                 m_ShouldActivateNextUpdate = false;
01096             }
01097 
01098             // Update Scrollbar if needed
01099             if (m_IsScrollbarUpdateRequired)
01100             {
01101                 UpdateScrollbar();
01102                 m_IsScrollbarUpdateRequired = false;
01103             }
01104 
01105             //if (!isFocused && !m_VerticalScrollbarEventHandler.isSelected)
01106             //{
01107             //    m_ForceDeactivation = true;
01108             //    DeactivateInputField();
01109 
01110             //    return;
01111             //}
01112 
01113             //if (!isFocused)
01114             //{
01115             //    GameObject currentSelection = EventSystem.current == null ? null : EventSystem.current.currentSelectedGameObject;
01116 
01117             //    if (currentSelection != null)
01118             //        Debug.Log("Current Selection is: " + EventSystem.current.currentSelectedGameObject);
01119             //    else
01120             //        Debug.Log("No GameObject is selected...");
01121             //}
01122 
01123 
01124             if (InPlaceEditing() || !isFocused)
01125                 return;
01126 
01127             //Debug.Log(this + " has focus...");
01128 
01129             AssignPositioningIfNeeded();
01130 
01131             if (m_Keyboard == null || !m_Keyboard.active)
01132             {
01133                 if (m_Keyboard != null)
01134                 {
01135                     if (!m_ReadOnly)
01136                         text = m_Keyboard.text;
01137 
01138                     if (m_Keyboard.status == TouchScreenKeyboard.Status.Canceled)
01139                         m_WasCanceled = true;
01140 
01141                     if (m_Keyboard.status == TouchScreenKeyboard.Status.Done)
01142                         OnSubmit(null);
01143                 }
01144 
01145                 OnDeselect(null);
01146                 return;
01147             }
01148 
01149             string val = m_Keyboard.text;
01150 
01151             if (m_Text != val)
01152             {
01153                 if (m_ReadOnly)
01154                 {
01155                     m_Keyboard.text = m_Text;
01156                 }
01157                 else
01158                 {
01159                     m_Text = "";
01160 
01161                     for (int i = 0; i < val.Length; ++i)
01162                     {
01163                         char c = val[i];
01164 
01165                         if (c == '\r' || (int)c == 3)
01166                             c = '\n';
01167 
01168                         if (onValidateInput != null)
01169                             c = onValidateInput(m_Text, m_Text.Length, c);
01170                         else if (characterValidation != CharacterValidation.None)
01171                             c = Validate(m_Text, m_Text.Length, c);
01172 
01173                         if (lineType == LineType.MultiLineSubmit && c == '\n')
01174                         {
01175                             m_Keyboard.text = m_Text;
01176 
01177                             OnSubmit(null);
01178                             OnDeselect(null);
01179                             return;
01180                         }
01181 
01182                         if (c != 0)
01183                             m_Text += c;
01184                     }
01185 
01186                     if (characterLimit > 0 && m_Text.Length > characterLimit)
01187                         m_Text = m_Text.Substring(0, characterLimit);
01188                     stringPositionInternal = stringSelectPositionInternal = m_Text.Length;
01189 
01190                     // Set keyboard text before updating label, as we might have changed it with validation
01191                     // and update label will take the old value from keyboard if we don't change it here
01192                     if (m_Text != val)
01193                         m_Keyboard.text = m_Text;
01194 
01195                     SendOnValueChangedAndUpdateLabel();
01196                 }
01197             }
01198 
01199 
01200             if (m_Keyboard.status == TouchScreenKeyboard.Status.Done)
01201             {
01202                 if (m_Keyboard.status == TouchScreenKeyboard.Status.Canceled)
01203                     m_WasCanceled = true;
01204 
01205                 OnDeselect(null);
01206             }
01207         }
