01210         private bool MayDrag(PointerEventData eventData)
01211         {
01212             return IsActive() &&
01213                    IsInteractable() &&
01214                    eventData.button == PointerEventData.InputButton.Left &&
01215                    m_TextComponent != null &&
01216                    m_Keyboard == null;
01217         }
01218 
01219         public virtual void OnBeginDrag(PointerEventData eventData)
01220         {
01221             if (!MayDrag(eventData))
01222                 return;
01223 
01224             m_UpdateDrag = true;
01225         }
01226 
01227         public virtual void OnDrag(PointerEventData eventData)
01228         {
01229             if (!MayDrag(eventData))
01230                 return;
01231 
01232             CaretPosition insertionSide;
01233             int insertionIndex = TMP_TextUtilities.GetCursorIndexFromPosition(m_TextComponent, eventData.position, eventData.pressEventCamera, out insertionSide);
01234 
01235             if (insertionSide == CaretPosition.Left)
01236                 stringSelectPositionInternal = GetStringIndexFromCaretPosition(insertionIndex);
01237             else if (insertionSide == CaretPosition.Right)
01238                 stringSelectPositionInternal = GetStringIndexFromCaretPosition(insertionIndex) + 1;
01239 
01240             caretSelectPositionInternal = GetCaretPositionFromStringIndex(stringSelectPositionInternal);
01241 
01242             MarkGeometryAsDirty();
01243 
01244             m_DragPositionOutOfBounds = !RectTransformUtility.RectangleContainsScreenPoint(textViewport, eventData.position, eventData.pressEventCamera);
01245             if (m_DragPositionOutOfBounds && m_DragCoroutine == null)
01246                 m_DragCoroutine = StartCoroutine(MouseDragOutsideRect(eventData));
01247 
01248             eventData.Use();
01249         }
01250 
01251         IEnumerator MouseDragOutsideRect(PointerEventData eventData)
01252         {
01253             while (m_UpdateDrag && m_DragPositionOutOfBounds)
01254             {
01255                 Vector2 localMousePos;
01256                 RectTransformUtility.ScreenPointToLocalPointInRectangle(textViewport, eventData.position, eventData.pressEventCamera, out localMousePos);
01257 
01258                 Rect rect = textViewport.rect;
01259 
01260                 if (multiLine)
01261                 {
01262                     if (localMousePos.y > rect.yMax)
01263                         MoveUp(true, true);
01264                     else if (localMousePos.y < rect.yMin)
01265                         MoveDown(true, true);
01266                 }
01267                 else
01268                 {
01269                     if (localMousePos.x < rect.xMin)
01270                         MoveLeft(true, false);
01271                     else if (localMousePos.x > rect.xMax)
01272                         MoveRight(true, false);
01273                 }
01274 
01275                 UpdateLabel();
01276 
01277                 float delay = multiLine ? kVScrollSpeed : kHScrollSpeed;
01278                 yield return new WaitForSeconds(delay);
01279                 //yield return new WaitForSecondsRealtime(delay); // Unity 5.4
01280             }
01281             m_DragCoroutine = null;
01282         }
01283 
01284         public virtual void OnEndDrag(PointerEventData eventData)
01285         {
01286             if (!MayDrag(eventData))
01287                 return;
01288 
01289             m_UpdateDrag = false;
01290         }
01291 
01292         public override void OnPointerDown(PointerEventData eventData)
01293         {
01294             if (!MayDrag(eventData))
01295                 return;
01296 
01297             EventSystem.current.SetSelectedGameObject(gameObject, eventData);
01298 
01299             bool hadFocusBefore = m_AllowInput;
01300             base.OnPointerDown(eventData);
01301 
01302             if (!InPlaceEditing())
01303             {
01304                 if (m_Keyboard == null || !m_Keyboard.active)
01305                 {
01306                     OnSelect(eventData);
01307                     return;
01308                 }
01309             }
01310 
01311             bool shift = Input.GetKey(KeyCode.LeftShift) || Input.GetKey(KeyCode.RightShift);
01312 
01313             // Check for Double Click
01314             bool isDoubleClick = false;
01315             float timeStamp = Time.unscaledTime;
01316 
01317             if (m_ClickStartTime + m_DoubleClickDelay > timeStamp)
01318                 isDoubleClick = true;   
01319 
01320             m_ClickStartTime = timeStamp;
01321 
01322             // Only set caret position if we didn't just get focus now.
01323             // Otherwise it will overwrite the select all on focus.
01324             if (hadFocusBefore || !m_OnFocusSelectAll)
01325             {
01326                 CaretPosition insertionSide;
01327                 int insertionIndex = TMP_TextUtilities.GetCursorIndexFromPosition(m_TextComponent, eventData.position, eventData.pressEventCamera, out insertionSide);
01328 
01329                 if (shift)
01330                 {
01331                     if (insertionSide == CaretPosition.Left)
01332                         stringSelectPositionInternal = GetStringIndexFromCaretPosition(insertionIndex);
01333                     else if (insertionSide == CaretPosition.Right)
01334                         stringSelectPositionInternal = GetStringIndexFromCaretPosition(insertionIndex) + 1;
01335                 }
01336                 else
01337                 {
01338                     if (insertionSide == CaretPosition.Left)
01339                         stringPositionInternal = stringSelectPositionInternal = GetStringIndexFromCaretPosition(insertionIndex);
01340                     else if (insertionSide == CaretPosition.Right)
01341                         stringPositionInternal = stringSelectPositionInternal = GetStringIndexFromCaretPosition(insertionIndex) + 1;
01342                 }
01343 
01344 
01345                 if (isDoubleClick)
01346                 {
01347                     int wordIndex = TMP_TextUtilities.FindIntersectingWord(m_TextComponent, eventData.position, eventData.pressEventCamera);
01348 
01349                     if (wordIndex != -1)
01350                     {
01351                         // Select current word
01352                         caretPositionInternal = m_TextComponent.textInfo.wordInfo[wordIndex].firstCharacterIndex;
01353                         caretSelectPositionInternal = m_TextComponent.textInfo.wordInfo[wordIndex].lastCharacterIndex + 1;
01354 
01355                         stringPositionInternal = GetStringIndexFromCaretPosition(caretPositionInternal);
01356                         stringSelectPositionInternal = GetStringIndexFromCaretPosition(caretSelectPositionInternal);
01357                     }
01358                     else
01359                     {
01360                         // Select current character
01361                         caretPositionInternal = GetCaretPositionFromStringIndex(stringPositionInternal);
01362 
01363                         stringSelectPositionInternal += 1;
01364                         caretSelectPositionInternal = caretPositionInternal + 1;
01365                         caretSelectPositionInternal = GetCaretPositionFromStringIndex(stringSelectPositionInternal);
01366                     }
01367                 }
01368                 else
01369                 {
01370                     caretPositionInternal = caretSelectPositionInternal = GetCaretPositionFromStringIndex(stringPositionInternal);
01371                 }
01372             }
01373 
01374             UpdateLabel();
01375             eventData.Use();
01376         }
01377 
01378         protected enum EditState
01379         {
01380             Continue,
01381             Finish
01382         }
01383 
01384         protected EditState KeyPressed(Event evt)
01385         {
01386             var currentEventModifiers = evt.modifiers;
01387             RuntimePlatform rp = Application.platform;
01388             bool isMac = (rp == RuntimePlatform.OSXEditor || rp == RuntimePlatform.OSXPlayer);
01389             bool ctrl = isMac ? (currentEventModifiers & EventModifiers.Command) != 0 : (currentEventModifiers & EventModifiers.Control) != 0;
01390             bool shift = (currentEventModifiers & EventModifiers.Shift) != 0;
01391             bool alt = (currentEventModifiers & EventModifiers.Alt) != 0;
01392             bool ctrlOnly = ctrl && !alt && !shift;
01393 
01394             switch (evt.keyCode)
01395             {
01396                 case KeyCode.Backspace:
01397                     {
01398                         Backspace();
01399                         return EditState.Continue;
01400                     }
01401 
01402                 case KeyCode.Delete:
01403                     {
01404                         ForwardSpace();
01405                         return EditState.Continue;
01406                     }
01407 
01408                 case KeyCode.Home:
01409                     {
01410                         MoveToStartOfLine(shift, ctrl);
01411                         return EditState.Continue;
01412                     }
01413 
01414                 case KeyCode.End:
01415                     {
01416                         MoveToEndOfLine(shift, ctrl);
01417                         return EditState.Continue;
01418                     }
01419 
01420                 // Select All
01421                 case KeyCode.A:
01422                     {
01423                         if (ctrlOnly)
01424                         {
01425                             SelectAll();
01426                             return EditState.Continue;
01427                         }
01428                         break;
01429                     }
01430 
01431                 // Copy
01432                 case KeyCode.C:
01433                     {
01434                         if (ctrlOnly)
01435                         {
01436                             if (inputType != InputType.Password)
01437                                 clipboard = GetSelectedString();
01438                             else
01439                                 clipboard = "";
01440                             return EditState.Continue;
01441                         }
01442                         break;
01443                     }
01444 
01445                 // Paste
01446                 case KeyCode.V:
01447                     {
01448                         if (ctrlOnly)
01449                         {
01450                             Append(clipboard);
01451                             return EditState.Continue;
01452                         }
01453                         break;
01454                     }
01455 
01456                 // Cut
01457                 case KeyCode.X:
01458                     {
01459                         if (ctrlOnly)
01460                         {
01461                             if (inputType != InputType.Password)
01462                                 clipboard = GetSelectedString();
01463                             else
01464                                 clipboard = "";
01465                             Delete();
01466                             SendOnValueChangedAndUpdateLabel();
01467                             return EditState.Continue;
01468                         }
01469                         break;
01470                     }
01471 
01472                 case KeyCode.LeftArrow:
01473                     {
01474                         MoveLeft(shift, ctrl);
01475                         return EditState.Continue;
01476                     }
01477 
01478                 case KeyCode.RightArrow:
01479                     {
01480                         MoveRight(shift, ctrl);
01481                         return EditState.Continue;
01482                     }
01483 
01484                 case KeyCode.UpArrow:
01485                     {
01486                         MoveUp(shift);
01487                         return EditState.Continue;
01488                     }
01489 
01490                 case KeyCode.DownArrow:
01491                     {
01492                         MoveDown(shift);
01493                         return EditState.Continue;
01494                     }
01495 
01496                 case KeyCode.PageUp:
01497                     {
01498                         MovePageUp(shift);
01499                         return EditState.Continue;
01500                     }
01501 
01502                 case KeyCode.PageDown:
01503                     {
01504                         MovePageDown(shift);
01505                         return EditState.Continue;
01506                     }
01507 
01508                 // Submit
01509                 case KeyCode.Return:
01510                 case KeyCode.KeypadEnter:
01511                     {
01512                         if (lineType != LineType.MultiLineNewline)
01513                         {
01514                             return EditState.Finish;
01515                         }
01516                         break;
01517                     }
01518 
01519                 case KeyCode.Escape:
01520                     {
01521                         m_WasCanceled = true;
01522                         return EditState.Finish;
01523                     }
01524             }
01525 
01526             char c = evt.character;
01527 
01528             // Don't allow return chars or tabulator key to be entered into single line fields.
01529             if (!multiLine && (c == '\t' || c == '\r' || c == 10))
01530                 return EditState.Continue;
01531 
01532             // Convert carriage return and end-of-text characters to newline.
01533             if (c == '\r' || (int)c == 3)
01534                 c = '\n';
01535 
01536             if (IsValidChar(c))
01537             {
01538                 Append(c);
01539             }
01540 
01541             if (c == 0)
01542             {
01543                 if (Input.compositionString.Length > 0)
01544                 {
01545                     UpdateLabel();
01546                 }
01547             }
01548             return EditState.Continue;
01549         }
01550 
01551         private bool IsValidChar(char c)
01552         {
01553             // Delete key on mac
01554             if ((int)c == 127)
01555                 return false;
01556             // Accept newline and tab
01557             if (c == '\t' || c == '\n')
01558                 return true;
01559 
01560             return m_TextComponent.font.HasCharacter(c, true);
01561         }
01562 
01566         private Event m_ProcessingEvent = new Event();
01567 
01568         public void ProcessEvent(Event e)
01569         {
01570             KeyPressed(e);
01571         }
01572 
01573 
01578         public virtual void OnUpdateSelected(BaseEventData eventData)
01579         {
01580             if (!isFocused)
01581                 return;
01582 
01583             bool consumedEvent = false;
01584             while (Event.PopEvent(m_ProcessingEvent))
01585             {
01586                 if (m_ProcessingEvent.rawType == EventType.KeyDown)
01587                 {
01588                     consumedEvent = true;
01589                     var shouldContinue = KeyPressed(m_ProcessingEvent);
01590                     if (shouldContinue == EditState.Finish)
01591                     {
01592                         SendOnSubmit();
01593                         DeactivateInputField();
01594                         break;
01595                     }
01596                 }
01597 
01598                 switch (m_ProcessingEvent.type)
01599                 {
01600                     case EventType.ValidateCommand:
01601                     case EventType.ExecuteCommand:
01602                         switch (m_ProcessingEvent.commandName)
01603                         {
01604                             case "SelectAll":
01605                                 SelectAll();
01606                                 consumedEvent = true;
01607                                 break;
01608                         }
01609                         break;
01610                 }
01611             }
01612 
01613             if (consumedEvent)
01614                 UpdateLabel();
01615 
01616             eventData.Use();
01617         }
01618 
01619 
01624         public virtual void OnScroll(PointerEventData eventData)
01625         {
01626             if (m_TextComponent.preferredHeight < m_TextViewport.rect.height) return;
01627 
01628             float scrollDirection = -eventData.scrollDelta.y;
01629 
01630             m_ScrollPosition = m_ScrollPosition + (1f / m_TextComponent.textInfo.lineCount) * scrollDirection * m_ScrollSensitivity;
01631 
01632             m_ScrollPosition = Mathf.Clamp01(m_ScrollPosition);
01633 
01634             AdjustTextPositionRelativeToViewport(m_ScrollPosition);
01635 
01636             // Disable focus until user re-selected the input field.
01637             m_AllowInput = false;
01638 
01639             if (m_VerticalScrollbar)
01640             {
01641                 m_IsUpdatingScrollbarValues = true;
01642                 m_VerticalScrollbar.value = m_ScrollPosition;
01643                 //m_VerticalScrollbar.numberOfSteps = (int)(m_TextComponent.textInfo.lineCount / scrollSensitivity);
01644             }
01645 
01646             //Debug.Log("Scroll Position:" + m_ScrollPosition);
01647         }
01648 
01649 
01650         private string GetSelectedString()
01651         {
01652             if (!hasSelection)
01653                 return "";
01654 
01655             int startPos = stringPositionInternal;
01656             int endPos = stringSelectPositionInternal;
01657 
01658             // Ensure pos is always less then selPos to make the code simpler
01659             if (startPos > endPos)
01660             {
01661                 int temp = startPos;
01662                 startPos = endPos;
01663                 endPos = temp;
01664             }
01665 
01666             //for (int i = m_CaretPosition; i < m_CaretSelectPosition; i++)
01667             //{
01668             //    Debug.Log("Character [" + m_TextComponent.textInfo.characterInfo[i].character + "] using Style [" + m_TextComponent.textInfo.characterInfo[i].style + "] has been selected.");
01669             //}
01670 
01671 
01672             return text.Substring(startPos, endPos - startPos);
01673         }
01674 
01675         private int FindtNextWordBegin()
01676         {
01677             if (stringSelectPositionInternal + 1 >= text.Length)
01678                 return text.Length;
01679 
01680             int spaceLoc = text.IndexOfAny(kSeparators, stringSelectPositionInternal + 1);
01681 
01682             if (spaceLoc == -1)
01683                 spaceLoc = text.Length;
01684             else
01685                 spaceLoc++;
01686 
01687             return spaceLoc;
01688         }
