01690         private void MoveRight(bool shift, bool ctrl)
01691         {
01692             if (hasSelection && !shift)
01693             {
01694                 // By convention, if we have a selection and move right without holding shift,
01695                 // we just place the cursor at the end.
01696                 stringPositionInternal = stringSelectPositionInternal = Mathf.Max(stringPositionInternal, stringSelectPositionInternal);
01697                 caretPositionInternal = caretSelectPositionInternal = GetCaretPositionFromStringIndex(stringSelectPositionInternal);
01698 
01699                 #if TMP_DEBUG_MODE
01700                 Debug.Log("Caret Position: " + caretPositionInternal + " Selection Position: " + caretSelectPositionInternal + "  String Position: " + stringPositionInternal + " String Select Position: " + stringSelectPositionInternal);
01701                 #endif
01702                 return;
01703             }
01704 
01705             int position;
01706             if (ctrl)
01707                 position = FindtNextWordBegin();
01708             else
01709             {
01710                 if (m_isRichTextEditingAllowed)
01711                     position = stringSelectPositionInternal + 1;
01712                 else
01713                     position = GetStringIndexFromCaretPosition(caretSelectPositionInternal + 1);
01714 
01715             }
01716 
01717             if (shift)
01718             {
01719                 stringSelectPositionInternal = position;
01720                 caretSelectPositionInternal = GetCaretPositionFromStringIndex(stringSelectPositionInternal);
01721             }
01722             else
01723             {
01724                 stringSelectPositionInternal = stringPositionInternal = position;
01725                 caretSelectPositionInternal = caretPositionInternal = GetCaretPositionFromStringIndex(stringSelectPositionInternal);
01726             }
01727 
01728             #if TMP_DEBUG_MODE
01729             Debug.Log("Caret Position: " + caretPositionInternal + "  Selection Position: " + caretSelectPositionInternal + "  String Position: " + stringPositionInternal + "  String Select Position: " + stringSelectPositionInternal);
01730             #endif
01731         }
01732 
01733         private int FindtPrevWordBegin()
01734         {
01735             if (stringSelectPositionInternal - 2 < 0)
01736                 return 0;
01737 
01738             int spaceLoc = text.LastIndexOfAny(kSeparators, stringSelectPositionInternal - 2);
01739 
01740             if (spaceLoc == -1)
01741                 spaceLoc = 0;
01742             else
01743                 spaceLoc++;
01744 
01745             return spaceLoc;
01746         }
