01791 
01792         private int LineUpCharacterPosition(int originalPos, bool goToFirstChar)
01793         {
01794             if (originalPos >= m_TextComponent.textInfo.characterCount)
01795                 originalPos -= 1;
01796 
01797             TMP_CharacterInfo originChar = m_TextComponent.textInfo.characterInfo[originalPos];
01798             int originLine = originChar.lineNumber;
01799 
01800             // We are on the first line return first character
01801             if (originLine - 1 < 0)
01802                 return goToFirstChar ? 0 : originalPos;
01803 
01804             int endCharIdx = m_TextComponent.textInfo.lineInfo[originLine].firstCharacterIndex - 1;
01805 
01806             int closest = -1;
01807             float distance = TMP_Math.FLOAT_MAX;
01808             float range = 0;
01809 
01810             for (int i = m_TextComponent.textInfo.lineInfo[originLine - 1].firstCharacterIndex; i < endCharIdx; ++i)
01811             {
01812                 TMP_CharacterInfo currentChar = m_TextComponent.textInfo.characterInfo[i];
01813 
01814                 float d = originChar.origin - currentChar.origin;
01815                 float r = d / (currentChar.xAdvance - currentChar.origin);
01816 
01817                 if (r >= 0 && r <= 1)
01818                 {
01819                     if (r < 0.5f)
01820                         return i;
01821                     else
01822                         return i + 1;
01823                 }
01824 
01825                 d = Mathf.Abs(d);
01826 
01827                 if (d < distance)
01828                 {
01829                     closest = i;
01830                     distance = d;
01831                     range = r;
01832                 }
01833             }
01834 
01835             if (closest == -1) return endCharIdx;
01836 
01837             //Debug.Log("Returning nearest character with Range = " + range);
01838 
01839             if (range < 0.5f)
01840                 return closest;
01841             else
01842                 return closest + 1;
01843         }
01844 
01845 
01846         private int LineDownCharacterPosition(int originalPos, bool goToLastChar)
01847         {
01848             if (originalPos >= m_TextComponent.textInfo.characterCount)
01849                 return m_TextComponent.textInfo.characterCount - 1; // text.Length;
01850 
01851             TMP_CharacterInfo originChar = m_TextComponent.textInfo.characterInfo[originalPos];
01852             int originLine = originChar.lineNumber;
01853 
01855             if (originLine + 1 >= m_TextComponent.textInfo.lineCount)
01856                 return goToLastChar ? m_TextComponent.textInfo.characterCount - 1 : originalPos;
01857 
01858             // Need to determine end line for next line.
01859             int endCharIdx = m_TextComponent.textInfo.lineInfo[originLine + 1].lastCharacterIndex;
01860 
01861             int closest = -1;
01862             float distance = TMP_Math.FLOAT_MAX;
01863             float range = 0;
01864 
01865             for (int i = m_TextComponent.textInfo.lineInfo[originLine + 1].firstCharacterIndex; i < endCharIdx; ++i)
01866             {
01867                 TMP_CharacterInfo currentChar = m_TextComponent.textInfo.characterInfo[i];
01868 
01869                 float d = originChar.origin - currentChar.origin;
01870                 float r = d / (currentChar.xAdvance - currentChar.origin);
01871 
01872                 if (r >= 0 && r <= 1)
01873                 {
01874                     if (r < 0.5f)
01875                         return i;
01876                     else
01877                         return i + 1;
01878                 }
01879 
01880                 d = Mathf.Abs(d);
01881 
01882                 if (d < distance)
01883                 {
01884                     closest = i;
01885                     distance = d;
01886                     range = r;
01887                 }
01888             }
01889 
01890             if (closest == -1) return endCharIdx;
01891 
01892             //Debug.Log("Returning nearest character with Range = " + range);
01893 
01894             if (range < 0.5f)
01895                 return closest;
01896             else
01897                 return closest + 1;
01898         }
