01901          private int PageUpCharacterPosition(int originalPos, bool goToFirstChar)
01902         {
01903             if (originalPos >= m_TextComponent.textInfo.characterCount)
01904                 originalPos -= 1;
01905 
01906             TMP_CharacterInfo originChar = m_TextComponent.textInfo.characterInfo[originalPos];
01907             int originLine = originChar.lineNumber;
01908 
01909             // We are on the first line return first character
01910             if (originLine - 1 < 0)
01911                 return goToFirstChar ? 0 : originalPos;
01912 
01913             float viewportHeight = m_TextViewport.rect.height;
01914 
01915             int newLine = originLine - 1;
01916             // Iterate through each subsequent line to find the first baseline that is not visible in the viewport.
01917             for (; newLine > 0; newLine--)
01918             {
01919                 if (m_TextComponent.textInfo.lineInfo[newLine].baseline > m_TextComponent.textInfo.lineInfo[originLine].baseline + viewportHeight)
01920                     break;
01921             }
01922 
01923             int endCharIdx = m_TextComponent.textInfo.lineInfo[newLine].lastCharacterIndex;
01924 
01925             int closest = -1;
01926             float distance = TMP_Math.FLOAT_MAX;
01927             float range = 0;
01928 
01929             for (int i = m_TextComponent.textInfo.lineInfo[newLine].firstCharacterIndex; i < endCharIdx; ++i)
01930             {
01931                 TMP_CharacterInfo currentChar = m_TextComponent.textInfo.characterInfo[i];
01932 
01933                 float d = originChar.origin - currentChar.origin;
01934                 float r = d / (currentChar.xAdvance - currentChar.origin);
01935 
01936                 if (r >= 0 && r <= 1)
01937                 {
01938                     if (r < 0.5f)
01939                         return i;
01940                     else
01941                         return i + 1;
01942                 }
01943 
01944                 d = Mathf.Abs(d);
01945 
01946                 if (d < distance)
01947                 {
01948                     closest = i;
01949                     distance = d;
01950                     range = r;
01951                 }
01952             }
01953 
01954             if (closest == -1) return endCharIdx;
01955 
01956             //Debug.Log("Returning nearest character with Range = " + range);
01957 
01958             if (range < 0.5f)
01959                 return closest;
01960             else
01961                 return closest + 1;
01962         }
