01964 
01965          private int PageDownCharacterPosition(int originalPos, bool goToLastChar)
01966         {
01967             if (originalPos >= m_TextComponent.textInfo.characterCount)
01968                 return m_TextComponent.textInfo.characterCount - 1;
01969 
01970             TMP_CharacterInfo originChar = m_TextComponent.textInfo.characterInfo[originalPos];
01971             int originLine = originChar.lineNumber;
01972 
01973             // We are on the last line return last character
01974             if (originLine + 1 >= m_TextComponent.textInfo.lineCount)
01975                 return goToLastChar ? m_TextComponent.textInfo.characterCount - 1 : originalPos;
01976 
01977             float viewportHeight = m_TextViewport.rect.height;
01978 
01979             int newLine = originLine + 1;
01980             // Iterate through each subsequent line to find the first baseline that is not visible in the viewport.
01981             for (; newLine < m_TextComponent.textInfo.lineCount - 1; newLine++)
01982             {
01983                 if (m_TextComponent.textInfo.lineInfo[newLine].baseline < m_TextComponent.textInfo.lineInfo[originLine].baseline - viewportHeight)
01984                     break;
01985             }
01986 
01987             // Need to determine end line for next line.
01988             int endCharIdx = m_TextComponent.textInfo.lineInfo[newLine].lastCharacterIndex;
01989 
01990             int closest = -1;
01991             float distance = TMP_Math.FLOAT_MAX;
01992             float range = 0;
01993 
01994             for (int i = m_TextComponent.textInfo.lineInfo[newLine].firstCharacterIndex; i < endCharIdx; ++i)
01995             {
01996                 TMP_CharacterInfo currentChar = m_TextComponent.textInfo.characterInfo[i];
01997 
01998                 float d = originChar.origin - currentChar.origin;
01999                 float r = d / (currentChar.xAdvance - currentChar.origin);
02000 
02001                 if (r >= 0 && r <= 1)
02002                 {
02003                     if (r < 0.5f)
02004                         return i;
02005                     else
02006                         return i + 1;
02007                 }
02008 
02009                 d = Mathf.Abs(d);
02010 
02011                 if (d < distance)
02012                 {
02013                     closest = i;
02014                     distance = d;
02015                     range = r;
02016                 }
02017             }
02018 
02019             if (closest == -1) return endCharIdx;
02020 
02021             if (range < 0.5f)
02022                 return closest;
02023             else
02024                 return closest + 1;
02025         }
