02039                 // set caret to end of selection before we move it down.
02040                 caretPositionInternal = caretSelectPositionInternal = Mathf.Max(caretPositionInternal, caretSelectPositionInternal);
02041             }
02042 
02043             int position = multiLine ? LineDownCharacterPosition(caretSelectPositionInternal, goToLastChar) : m_TextComponent.textInfo.characterCount - 1; // text.Length;
02044 
02045             if (shift)
02046             {
02047                 caretSelectPositionInternal = position;
02048                 stringSelectPositionInternal = GetStringIndexFromCaretPosition(caretSelectPositionInternal);
02049             }
02050             else
02051             {
02052                 caretSelectPositionInternal = caretPositionInternal = position;
02053                 stringSelectPositionInternal = stringPositionInternal = GetStringIndexFromCaretPosition(caretSelectPositionInternal);
02054             }
02055 
02056             #if TMP_DEBUG_MODE
02057             Debug.Log("Caret Position: " + caretPositionInternal + " Selection Position: " + caretSelectPositionInternal + "  String Position: " + stringPositionInternal + " String Select Position: " + stringSelectPositionInternal);
