02103             {
02104                 // If we have a selection and press up without shift,
02105                 // set caret position to start of selection before we move it up.
02106                 caretPositionInternal = caretSelectPositionInternal = Mathf.Min(caretPositionInternal, caretSelectPositionInternal);
02107             }
02108 
02109             int position = multiLine ? PageUpCharacterPosition(caretSelectPositionInternal, goToFirstChar) : 0;
02110 
02111             if (shift)
02112             {
02113                 caretSelectPositionInternal = position;
02114                 stringSelectPositionInternal = GetStringIndexFromCaretPosition(caretSelectPositionInternal);
02115             }
02116             else
02117             {
02118                 caretSelectPositionInternal = caretPositionInternal = position;
02119                 stringSelectPositionInternal = stringPositionInternal = GetStringIndexFromCaretPosition(caretSelectPositionInternal);
02120             }
02121 
