02124             //int currentLine = m_TextComponent.textInfo.characterInfo[position].lineNumber;
02125             //float lineAscender = m_TextComponent.textInfo.lineInfo[currentLine].ascender;
02126 
02127             // Adjust text area up or down if not in single line mode.
02128             if (m_LineType != LineType.SingleLine)
02129             {
02130                 float offset = m_TextViewport.rect.height; // m_TextViewport.rect.yMax - (m_TextComponent.rectTransform.anchoredPosition.y + lineAscender);
