02133                 float topViewportBounds = m_TextViewport.position.y + m_TextViewport.rect.yMax;
02134 
02135                 offset = topViewportBounds > topTextBounds + offset ? offset : topViewportBounds - topTextBounds;
02136 
02137                 m_TextComponent.rectTransform.anchoredPosition += new Vector2(0, offset);
02138                 AssignPositioningIfNeeded();
02139                 m_IsScrollbarUpdateRequired = true;
02140             }
02141 
