02200 
02201         private void Delete()
02202         {
02203             if (m_ReadOnly)
02204                 return;
02205 
02206             if (stringPositionInternal == stringSelectPositionInternal)
02207                 return;
02208 
02209             if (m_isRichTextEditingAllowed || m_isSelectAll)
02210             {
02211                 // Handling of Delete when Rich Text is allowed.
02212                 if (stringPositionInternal < stringSelectPositionInternal)
02213                 {
02214                     m_Text = text.Substring(0, stringPositionInternal) + text.Substring(stringSelectPositionInternal, text.Length - stringSelectPositionInternal);
02215                     stringSelectPositionInternal = stringPositionInternal;
02216                 }
02217                 else
02218                 {
02219                     m_Text = text.Substring(0, stringSelectPositionInternal) + text.Substring(stringPositionInternal, text.Length - stringPositionInternal);
02220                     stringPositionInternal = stringSelectPositionInternal;
02221                 }
02222 
02223                 m_isSelectAll = false;
02224             }
02225             else
02226             {
02227                 stringPositionInternal = GetStringIndexFromCaretPosition(caretPositionInternal);
02228                 stringSelectPositionInternal = GetStringIndexFromCaretPosition(caretSelectPositionInternal);
02229 
02230                 // Handling of Delete when Rich Text is not allowed.
02231                 if (caretPositionInternal < caretSelectPositionInternal)
02232                 {
02233                     m_Text = text.Substring(0, stringPositionInternal) + text.Substring(stringSelectPositionInternal, text.Length - stringSelectPositionInternal);
02234 
02235                     stringSelectPositionInternal = stringPositionInternal;
02236                     caretSelectPositionInternal = caretPositionInternal;
02237                 }
02238                 else
02239                 {
02240                     m_Text = text.Substring(0, stringSelectPositionInternal) + text.Substring(stringPositionInternal, text.Length - stringPositionInternal);
02241                     stringPositionInternal = stringSelectPositionInternal;
02242 
02243                     stringPositionInternal = stringSelectPositionInternal;
02244                     caretPositionInternal = caretSelectPositionInternal;
02245                 }
02246             }
02247 
02248             #if TMP_DEBUG_MODE
02249             Debug.Log("Caret Position: " + caretPositionInternal + " Selection Position: " + caretSelectPositionInternal + "  String Position: " + stringPositionInternal + " String Select Position: " + stringSelectPositionInternal);
02250             #endif
02251         }
02252 
02256         private void ForwardSpace()
02257         {
02258             if (m_ReadOnly)
02259                 return;
02260 
02261             if (hasSelection)
02262             {
02263                 Delete();
02264                 SendOnValueChangedAndUpdateLabel();
02265             }
02266             else
02267             {
02268                 if (m_isRichTextEditingAllowed)
02269                 {
02270                     if (stringPositionInternal < text.Length)
02271                     { 
02272                         m_Text = text.Remove(stringPositionInternal, 1);
02273 
02274                         SendOnValueChangedAndUpdateLabel();
02275                     }
02276                 }
02277                 else
02278                 {
02279                     if (caretPositionInternal < m_TextComponent.textInfo.characterCount - 1)
02280                     {
02281                         stringSelectPositionInternal = stringPositionInternal = GetStringIndexFromCaretPosition(caretPositionInternal);
02282                         m_Text = text.Remove(stringPositionInternal, 1);
02283 
02284                         SendOnValueChangedAndUpdateLabel();
02285                     }
02286                 }
02287             }
02288 
02289             #if TMP_DEBUG_MODE
02290             Debug.Log("Caret Position: " + caretPositionInternal + " Selection Position: " + caretSelectPositionInternal + "  String Position: " + stringPositionInternal + " String Select Position: " + stringSelectPositionInternal);
02291             #endif
02292         }
02293 
02297         private void Backspace()
02298         {
02299             if (m_ReadOnly)
02300                 return;
02301 
02302             if (hasSelection)
02303             {
02304                 Delete();
02305                 SendOnValueChangedAndUpdateLabel();
02306             }
02307             else
02308             {
02309                 if (m_isRichTextEditingAllowed)
02310                 {
02311                     if (stringPositionInternal > 0)
02312                     {
02313                         m_Text = text.Remove(stringPositionInternal - 1, 1);
02314                         stringSelectPositionInternal = stringPositionInternal = stringPositionInternal - 1;
02315 
02316                         m_isLastKeyBackspace = true;
02317 
02318                         SendOnValueChangedAndUpdateLabel();
02319                     }
02320                 }
02321                 else
02322                 {
02323                     if (caretPositionInternal > 0)
02324                     {
02325                         m_Text = text.Remove(GetStringIndexFromCaretPosition(caretPositionInternal - 1), 1);
02326                         caretSelectPositionInternal = caretPositionInternal = caretPositionInternal - 1;
02327                         stringSelectPositionInternal = stringPositionInternal = GetStringIndexFromCaretPosition(caretPositionInternal);
02328                     }
02329 
02330                     m_isLastKeyBackspace = true;
02331 
02332                     SendOnValueChangedAndUpdateLabel();
02333                 }
02334 
02335             }
02336 
02337             #if TMP_DEBUG_MODE
02338             Debug.Log("Caret Position: " + caretPositionInternal + " Selection Position: " + caretSelectPositionInternal + "  String Position: " + stringPositionInternal + " String Select Position: " + stringSelectPositionInternal);
02339             #endif
02340         }
02341 
02342 
02346         protected virtual void Append(string input)
02347         {
02348             if (m_ReadOnly)
02349                 return;
02350 
02351             if (!InPlaceEditing())
02352                 return;
02353 
02354             for (int i = 0, imax = input.Length; i < imax; ++i)
02355             {
02356                 char c = input[i];
02357 
02358                 if (c >= ' ' || c == '\t' || c == '\r' || c == 10 || c == '\n')
02359                 {
02360                     Append(c);
02361                 }
02362             }
02363         }
02364 
02365         protected virtual void Append(char input)
02366         {
02367             if (m_ReadOnly)
02368                 return;
02369 
02370             if (!InPlaceEditing())
02371                 return;
02372 
02373             // If we have an input validator, validate the input first
02374             if (onValidateInput != null)
02375                 input = onValidateInput(text, stringPositionInternal, input);
02376             else if (characterValidation == CharacterValidation.CustomValidator)
02377             {
02378                 input = Validate(text, stringPositionInternal, input);
02379 
02380                 if (input == 0) return;
02381 
02382                 SendOnValueChanged();
02383                 UpdateLabel();
02384 
02385                 return;
02386             }
02387             else if (characterValidation != CharacterValidation.None)
02388                 input = Validate(text, stringPositionInternal, input);
02389 
02390 
02391 
02392             // If the input is invalid, skip it
02393             if (input == 0)
02394                 return;
02395 
02396             // Append the character and update the label
02397             Insert(input);
02398         }
02399 
02400 
02401         // Insert the character and update the label.
02402         private void Insert(char c)
02403         {
02404             if (m_ReadOnly)
02405                 return;
02406 
02407             string replaceString = c.ToString();
02408             Delete();
02409 
02410             // Can't go past the character limit
02411             if (characterLimit > 0 && text.Length >= characterLimit)
02412                 return;
02413 
02414             m_Text = text.Insert(m_StringPosition, replaceString);
02415             stringSelectPositionInternal = stringPositionInternal += replaceString.Length;
02416 
02417             SendOnValueChanged();
02418 
02419             #if TMP_DEBUG_MODE
02420             Debug.Log("Caret Position: " + caretPositionInternal + " Selection Position: " + caretSelectPositionInternal + "  String Position: " + stringPositionInternal + " String Select Position: " + stringSelectPositionInternal);
02421             #endif
02422         }
02423 
02424         private void SendOnValueChangedAndUpdateLabel()
02425         {
02426             SendOnValueChanged();
02427             UpdateLabel();
02428         }
02429 
