02482 
02486 
02487         protected void UpdateLabel()
02488         {
02489             if (m_TextComponent != null && m_TextComponent.font != null)
02490             {
02491                 // TextGenerator.Populate invokes a callback that's called for anything
02492                 // that needs to be updated when the data for that font has changed.
02493                 // This makes all Text components that use that font update their vertices.
02494                 // In turn, this makes the InputField that's associated with that Text component
02495                 // update its label by calling this UpdateLabel method.
02496                 // This is a recursive call we want to prevent, since it makes the InputField
02497                 // update based on font data that didn't yet finish executing, or alternatively
02498                 // hang on infinite recursion, depending on whether the cached value is cached
02499                 // before or after the calculation.
02500                 //
02501                 // This callback also occurs when assigning text to our Text component, i.e.,
02502                 // m_TextComponent.text = processed;
02503 
02504                 //m_PreventFontCallback = true;
02505 
02506                 string fullText;
02507                 if (Input.compositionString.Length > 0)
02508                     fullText = text.Substring(0, m_StringPosition) + Input.compositionString + text.Substring(m_StringPosition);
02509                 else
02510                     fullText = text;
02511 
02512                 string processed;
02513                 if (inputType == InputType.Password)
02514                     processed = new string(asteriskChar, fullText.Length);
02515                 else
02516                     processed = fullText;
02517 
02518                 bool isEmpty = string.IsNullOrEmpty(fullText);
02519 
02520                 if (m_Placeholder != null)
02521                     m_Placeholder.enabled = isEmpty; // && !isFocused;
02522 
02523                 // If not currently editing the text, set the visible range to the whole text.
02524                 // The UpdateLabel method will then truncate it to the part that fits inside the Text area.
02525                 // We can't do this when text is being edited since it would discard the current scroll,
02526                 // which is defined by means of the m_DrawStart and m_DrawEnd indices.
02527 
02528                 if (!isEmpty)
02529                 {
02530                 //    // Determine what will actually fit into the given line
02531                 //    Vector2 extents = m_TextComponent.rectTransform.rect.size;
02532 
02533                 //    var settings = m_TextComponent.GetGenerationSettings(extents);
02534                 //    settings.generateOutOfBounds = true;
02535 
02536                 //    cachedInputTextGenerator.Populate(processed, settings);
02537 
02538                 //    SetDrawRangeToContainCaretPosition(stringSelectPositionInternal - 1);
02539 
02540                 //    processed = processed.Substring(m_DrawStart, Mathf.Min(m_DrawEnd, processed.Length) - m_DrawStart);
02541 
02542                     SetCaretVisible();
02543                 }
02544 
02545                 m_TextComponent.text = processed + "\u200B"; // Extra space is added for Caret tracking.
02546                 MarkGeometryAsDirty();
02547 
02548                 // Scrollbar should be updated.
02549                 m_IsScrollbarUpdateRequired = true;
02550 
02551                 //m_PreventFontCallback = false;
02552             }
02553         }
02554 
