02556         //{
02557         //    if (m_DrawStart > stringPositionInternal || m_DrawStart > stringSelectPositionInternal)
02558         //        return false;
02559 
02560         //    if (m_DrawEnd < stringPositionInternal || m_DrawEnd < stringSelectPositionInternal)
02561         //        return false;
02562 
02563         //    return true;
02564         //}
