02566         void UpdateScrollbar()
02567         {
02568             // Update Scrollbar
02569             if (m_VerticalScrollbar)
02570             {
02571                 float size = m_TextViewport.rect.height / m_TextComponent.preferredHeight;
02572 
02573                 m_IsUpdatingScrollbarValues = true;
02574 
02575                 m_VerticalScrollbar.size = size;
02576 
02577                 m_ScrollPosition = m_VerticalScrollbar.value = m_TextComponent.rectTransform.anchoredPosition.y / (m_TextComponent.preferredHeight - m_TextViewport.rect.height);
02578 
02579                 //m_VerticalScrollbar.numberOfSteps = (int)(m_TextComponent.textInfo.lineCount / 0.25f); // Replace by scroll sensitivity.
02580 
02581                 //Debug.Log("Updating Scrollbar... Value: " + m_VerticalScrollbar.value);
02582             }
02583         }
