02585 
02590         void OnScrollbarValueChange(float value)
02591         {
02592             if (m_IsUpdatingScrollbarValues) { m_IsUpdatingScrollbarValues = false; return; }
02593 
02594             if (value < 0 || value > 1) return;
02595 
02596             AdjustTextPositionRelativeToViewport(value);
02597 
02598             m_ScrollPosition = value;
02599 
02600             //Debug.Log("Scrollbar value is: " + value + "  Transform POS: " + m_TextComponent.rectTransform.anchoredPosition);
02601         }
02602 
02607         void AdjustTextPositionRelativeToViewport (float relativePosition)
02608         {
02609             //Debug.Log("- Adjusting vertical text position to " + relativePosition);
02610 
02611             TMP_TextInfo textInfo = m_TextComponent.textInfo;
02612 
02613             // Check to make sure we have valid data and lines to query.
02614             if (textInfo == null || textInfo.lineInfo == null || textInfo.lineCount == 0 || textInfo.lineCount > textInfo.lineInfo.Length) return;
02615 
02616             //m_TextComponent.rectTransform.anchoredPosition = new Vector2(m_TextComponent.rectTransform.anchoredPosition.x, (textHeight - viewportHeight) * relativePosition);
02617             m_TextComponent.rectTransform.anchoredPosition = new Vector2(m_TextComponent.rectTransform.anchoredPosition.x, (m_TextComponent.preferredHeight - m_TextViewport.rect.height) * relativePosition);
02618 
02619             AssignPositioningIfNeeded();
02620 
02621             //Debug.Log("Text height: " + m_TextComponent.preferredHeight + "  Viewport height: " + m_TextViewport.rect.height + "  Adjusted RectTransform anchordedPosition:" + m_TextComponent.rectTransform.anchoredPosition + "  Text Bounds: " + m_TextComponent.bounds.ToString("f3"));
02622         }
