02624 
02625         private int GetCaretPositionFromStringIndex(int stringIndex)
02626         {
02627             int count = m_TextComponent.textInfo.characterCount;
02628 
02629             for (int i = 0; i < count; i++)
02630             {
02631                 if (m_TextComponent.textInfo.characterInfo[i].index >= stringIndex)
02632                     return i;
02633             }
02634 
02635             return count;
02636         }
02637 
02638         private int GetStringIndexFromCaretPosition(int caretPosition)
02639         {
02640             // Clamp values between 0 and character count.
02641             ClampCaretPos(ref caretPosition);
02642 
02643             return m_TextComponent.textInfo.characterInfo[caretPosition].index;
02644         }
