02727         private void AssignPositioningIfNeeded()
02728         {
02729             if (m_TextComponent != null && caretRectTrans != null &&
02730                 (caretRectTrans.localPosition != m_TextComponent.rectTransform.localPosition ||
02731                  caretRectTrans.localRotation != m_TextComponent.rectTransform.localRotation ||
02732                  caretRectTrans.localScale != m_TextComponent.rectTransform.localScale ||
02733                  caretRectTrans.anchorMin != m_TextComponent.rectTransform.anchorMin ||
02734                  caretRectTrans.anchorMax != m_TextComponent.rectTransform.anchorMax ||
02735                  caretRectTrans.anchoredPosition != m_TextComponent.rectTransform.anchoredPosition ||
02736                  caretRectTrans.sizeDelta != m_TextComponent.rectTransform.sizeDelta ||
02737                  caretRectTrans.pivot != m_TextComponent.rectTransform.pivot))
02738             {
02739                 caretRectTrans.localPosition = m_TextComponent.rectTransform.localPosition;
02740                 caretRectTrans.localRotation = m_TextComponent.rectTransform.localRotation;
02741                 caretRectTrans.localScale = m_TextComponent.rectTransform.localScale;
02742                 caretRectTrans.anchorMin = m_TextComponent.rectTransform.anchorMin;
02743                 caretRectTrans.anchorMax = m_TextComponent.rectTransform.anchorMax;
02744                 caretRectTrans.anchoredPosition = m_TextComponent.rectTransform.anchoredPosition;
02745                 caretRectTrans.sizeDelta = m_TextComponent.rectTransform.sizeDelta;
02746                 caretRectTrans.pivot = m_TextComponent.rectTransform.pivot;
02747 
02748                 // Get updated world corners of viewport.
02749                 //m_TextViewport.GetLocalCorners(m_ViewportCorners);
02750             }
02751         }
