02753 
02754         private void OnFillVBO(Mesh vbo)
02755         {
02756             using (var helper = new VertexHelper())
02757             {
02758                 if (!isFocused && m_ResetOnDeActivation)
02759                 {
02760                     helper.FillMesh(vbo);
02761                     return;
02762                 }
02763 
02764                 if (isStringPositionDirty)
02765                 {
02766                     stringPositionInternal = GetStringIndexFromCaretPosition(m_CaretPosition);
02767                     stringSelectPositionInternal = GetStringIndexFromCaretPosition(m_CaretSelectPosition);
02768                     isStringPositionDirty = false;
02769                 }
02770 
02771                 if (!hasSelection)
02772                 {
02773                     GenerateCaret(helper, Vector2.zero);
02774                     SendOnEndTextSelection();
02775                 }
02776                 else
02777                 {
02778                     GenerateHightlight(helper, Vector2.zero);
02779                     SendOnTextSelection();
02780                 }
02781 
02782                 helper.FillMesh(vbo);
02783             }
02784         }
02785 
02786 
02787         private void GenerateCaret(VertexHelper vbo, Vector2 roundingOffset)
02788         {
02789             if (!m_CaretVisible)
02790                 return;
02791 
02792             if (m_CursorVerts == null)
02793             {
02794                 CreateCursorVerts();
02795             }
02796 
02797             float width = m_CaretWidth;
02798 
02799             // Optimize to only update the caret position when needed.
02800             //
02801             //
02802 
02803             int characterCount = m_TextComponent.textInfo.characterCount;
02804             Vector2 startPosition = Vector2.zero;
02805             float height = 0;
02806             TMP_CharacterInfo currentCharacter;
02807 
02808             // Get the position of the Caret based on position in the string.
02809             caretPositionInternal = GetCaretPositionFromStringIndex(stringPositionInternal);
02810 
02811             if (caretPositionInternal == 0)
02812             {
02813                 currentCharacter = m_TextComponent.textInfo.characterInfo[0];
02814                 startPosition = new Vector2(currentCharacter.origin, currentCharacter.descender);
02815                 height = currentCharacter.ascender - currentCharacter.descender;
02816             }
02817             else if (caretPositionInternal < characterCount)
02818             {
02819                 currentCharacter = m_TextComponent.textInfo.characterInfo[caretPositionInternal];
02820                 startPosition = new Vector2(currentCharacter.origin, currentCharacter.descender);
02821                 height = currentCharacter.ascender - currentCharacter.descender;
02822             }
02823             else
02824             {
02825                 currentCharacter = m_TextComponent.textInfo.characterInfo[characterCount - 1];
02826                 startPosition = new Vector2(currentCharacter.xAdvance, currentCharacter.descender);
02827                 height = currentCharacter.ascender - currentCharacter.descender;
02828             }
02829 
02830             //Debug.Log("String Char [" + m_Text[m_StringPosition] + "] at Index:" + m_StringPosition + "  Caret Char [" + currentCharacter.character + "] at Index:" + caretPositionInternal);
02831 
02832             // Adjust the position of the RectTransform based on the caret position in the viewport (only if we have focus).
02833             if (isFocused && startPosition != m_LastPosition || m_forceRectTransformAdjustment)
02834                 AdjustRectTransformRelativeToViewport(startPosition, height, currentCharacter.isVisible);
02835 
02836             m_LastPosition = startPosition;
02837 
02838             // Clamp Caret height
02839             float top = startPosition.y + height;
02840             float bottom = top - height; // Mathf.Min(height, m_TextComponent.rectTransform.rect.height);
02841 
02842             m_CursorVerts[0].position = new Vector3(startPosition.x, bottom, 0.0f);
02843             m_CursorVerts[1].position = new Vector3(startPosition.x, top, 0.0f);
02844             m_CursorVerts[2].position = new Vector3(startPosition.x + width, top, 0.0f);
02845             m_CursorVerts[3].position = new Vector3(startPosition.x + width, bottom, 0.0f);
02846 
02847             // Set Vertex Color for the caret color.
02848             m_CursorVerts[0].color = caretColor;
02849             m_CursorVerts[1].color = caretColor;
02850             m_CursorVerts[2].color = caretColor;
02851             m_CursorVerts[3].color = caretColor;
02852 
02853             vbo.AddUIVertexQuad(m_CursorVerts);
02854 
02855             int screenHeight = Screen.height;
02856             // Removed multiple display support until it supports none native resolutions(case 741751)
02857             //int displayIndex = m_TextComponent.canvas.targetDisplay;
02858             //if (Screen.fullScreen && displayIndex < Display.displays.Length)
02859             //    screenHeight = Display.displays[displayIndex].renderingHeight;
02860 
02861             startPosition.y = screenHeight - startPosition.y;
02862             Input.compositionCursorPos = startPosition;
02863 
02864             //Debug.Log("Text Position: " + m_TextComponent.rectTransform.position + "  Local Position: " + m_TextComponent.rectTransform.localPosition);
02865         }
02866 
02867 
02868         private void CreateCursorVerts()
02869         {
02870             m_CursorVerts = new UIVertex[4];
02871 
02872             for (int i = 0; i < m_CursorVerts.Length; i++)
02873             {
02874                 m_CursorVerts[i] = UIVertex.simpleVert;
02875                 m_CursorVerts[i].uv0 = Vector2.zero;
02876             }
02877         }
02878 
02879 
02880         private void GenerateHightlight(VertexHelper vbo, Vector2 roundingOffset)
02881         {
02882             TMP_TextInfo textInfo = m_TextComponent.textInfo;
02883 
02884             caretPositionInternal = m_CaretPosition = GetCaretPositionFromStringIndex(stringPositionInternal);
02885             caretSelectPositionInternal = m_CaretSelectPosition = GetCaretPositionFromStringIndex(stringSelectPositionInternal);
02886 
02887             //Debug.Log("StringPosition:" + caretPositionInternal + "  StringSelectPosition:" + caretSelectPositionInternal);
02888 
02889             // Adjust text RectTranform position to make sure it is visible in viewport.
02890             Vector2 caretPosition;
02891             float height = 0;
02892             if (caretSelectPositionInternal < textInfo.characterCount)
02893             {
02894                 caretPosition = new Vector2(textInfo.characterInfo[caretSelectPositionInternal].origin, textInfo.characterInfo[caretSelectPositionInternal].descender);
02895                 height = textInfo.characterInfo[caretSelectPositionInternal].ascender - textInfo.characterInfo[caretSelectPositionInternal].descender;
02896             }
02897             else
02898             {
02899                 caretPosition = new Vector2(textInfo.characterInfo[caretSelectPositionInternal - 1].xAdvance, textInfo.characterInfo[caretSelectPositionInternal - 1].descender);
02900                 height = textInfo.characterInfo[caretSelectPositionInternal - 1].ascender - textInfo.characterInfo[caretSelectPositionInternal - 1].descender;
02901             }
02902 
02903             // TODO: Don't adjust the position of the RectTransform if Reset On Deactivation is disabled
02904             // and we just selected the Input Field again.
02905             AdjustRectTransformRelativeToViewport(caretPosition, height, true);
02906 
02907             int startChar = Mathf.Max(0, caretPositionInternal);
02908             int endChar = Mathf.Max(0, caretSelectPositionInternal);
02909 
02910             // Ensure pos is always less then selPos to make the code simpler
02911             if (startChar > endChar)
02912             {
02913                 int temp = startChar;
02914                 startChar = endChar;
02915                 endChar = temp;
02916             }
02917 
02918             endChar -= 1;
02919 
02920             //Debug.Log("Updating Highlight... Caret Position: " + startChar + " Caret Select POS: " + endChar);
02921 
02922 
02923             int currentLineIndex = textInfo.characterInfo[startChar].lineNumber;
02924             int nextLineStartIdx = textInfo.lineInfo[currentLineIndex].lastCharacterIndex;
02925 
02926             UIVertex vert = UIVertex.simpleVert;
02927             vert.uv0 = Vector2.zero;
02928             vert.color = selectionColor;
02929 
02930             int currentChar = startChar;
02931             while (currentChar <= endChar && currentChar < textInfo.characterCount)
02932             {
02933                 if (currentChar == nextLineStartIdx || currentChar == endChar)
02934                 {
02935                     TMP_CharacterInfo startCharInfo = textInfo.characterInfo[startChar];
02936                     TMP_CharacterInfo endCharInfo = textInfo.characterInfo[currentChar];
02937 
02938                     // Extra check to handle Carriage Return
02939                     if (currentChar > 0 && endCharInfo.character == 10 && textInfo.characterInfo[currentChar - 1].character == 13)
02940                         endCharInfo = textInfo.characterInfo[currentChar - 1];
02941 
02942                     Vector2 startPosition = new Vector2(startCharInfo.origin, textInfo.lineInfo[currentLineIndex].ascender);
02943                     Vector2 endPosition = new Vector2(endCharInfo.xAdvance, textInfo.lineInfo[currentLineIndex].descender);
02944 
02945                     var startIndex = vbo.currentVertCount;
02946                     vert.position = new Vector3(startPosition.x, endPosition.y, 0.0f);
02947                     vbo.AddVert(vert);
02948 
02949                     vert.position = new Vector3(endPosition.x, endPosition.y, 0.0f);
02950                     vbo.AddVert(vert);
02951 
02952                     vert.position = new Vector3(endPosition.x, startPosition.y, 0.0f);
02953                     vbo.AddVert(vert);
02954 
02955                     vert.position = new Vector3(startPosition.x, startPosition.y, 0.0f);
02956                     vbo.AddVert(vert);
02957 
02958                     vbo.AddTriangle(startIndex, startIndex + 1, startIndex + 2);
02959                     vbo.AddTriangle(startIndex + 2, startIndex + 3, startIndex + 0);
02960 
02961                     startChar = currentChar + 1;
02962                     currentLineIndex++;
02963 
02964                     if (currentLineIndex < textInfo.lineCount)
02965                         nextLineStartIdx = textInfo.lineInfo[currentLineIndex].lastCharacterIndex;
02966                 }
02967                 currentChar++;
02968             }
02969 
02970             // Scrollbar should be updated.
02971             m_IsScrollbarUpdateRequired = true;
02972         }
02973 
02974 
02981         private void AdjustRectTransformRelativeToViewport(Vector2 startPosition, float height, bool isCharVisible)
02982         {
02983             //Debug.Log("Adjusting transform position relative to viewport.");
02984 
02985             float viewportMin = m_TextViewport.rect.xMin;
02986             float viewportMax = m_TextViewport.rect.xMax;
02987 
02988             //Debug.Log("Viewport Rect: " + viewportMax + "  Start Position: " + startPosition);
02989             // Adjust the position of the RectTransform based on the caret position in the viewport.
02990             float rightOffset = viewportMax - (m_TextComponent.rectTransform.anchoredPosition.x + startPosition.x + m_TextComponent.margin.z + m_CaretWidth);
02991             if (rightOffset < 0f)
02992             {
02993                 if (!multiLine || (multiLine && isCharVisible))
02994                 {
02995                     //Debug.Log("Shifting text to the right by " + rightOffset.ToString("f3"));
02996                     m_TextComponent.rectTransform.anchoredPosition += new Vector2(rightOffset, 0);
02997 
02998                     AssignPositioningIfNeeded();
02999                 }
03000             }
03001 
03002             float leftOffset = (m_TextComponent.rectTransform.anchoredPosition.x + startPosition.x - m_TextComponent.margin.x) - viewportMin;
03003             if (leftOffset < 0f)
03004             {
03005                 //Debug.Log("Shifting text to the left by " + leftOffset.ToString("f3"));
03006                 m_TextComponent.rectTransform.anchoredPosition += new Vector2(-leftOffset, 0);
03007                 AssignPositioningIfNeeded();
03008             }
03009 
03010 
03011             // Adjust text area up or down if not in single line mode.
03012             if (m_LineType != LineType.SingleLine)
03013             {
03014                 float topOffset = m_TextViewport.rect.yMax - (m_TextComponent.rectTransform.anchoredPosition.y + startPosition.y + height);
03015                 if (topOffset < -0.0001f)
03016                 {
03017                     m_TextComponent.rectTransform.anchoredPosition += new Vector2(0, topOffset);
03018                     AssignPositioningIfNeeded();
03019                     m_IsScrollbarUpdateRequired = true;
03020                 }
03021 
03022                 float bottomOffset = (m_TextComponent.rectTransform.anchoredPosition.y + startPosition.y) - m_TextViewport.rect.yMin;
03023                 if (bottomOffset < 0f)
03024                 {
03025                     m_TextComponent.rectTransform.anchoredPosition -= new Vector2(0, bottomOffset);
03026                     AssignPositioningIfNeeded();
03027                     m_IsScrollbarUpdateRequired = true;
03028                 }
03029             }
03030 
03031             // Special handling of backspace
03032             if (m_isLastKeyBackspace)
03033             {
03034                 float firstCharPosition = m_TextComponent.rectTransform.anchoredPosition.x + m_TextComponent.textInfo.characterInfo[0].origin - m_TextComponent.margin.x;
03035                 float lastCharPosition = m_TextComponent.rectTransform.anchoredPosition.x + m_TextComponent.textInfo.characterInfo[m_TextComponent.textInfo.characterCount - 1].origin + m_TextComponent.margin.z;
03036 
03037                 // Check if caret is at the left most position of the viewport
03038                 if (m_TextComponent.rectTransform.anchoredPosition.x + startPosition.x <= viewportMin + 0.0001f)
03039                 {
03040                     if (firstCharPosition < viewportMin)
03041                     {
03042                         float offset = Mathf.Min((viewportMax - viewportMin) / 2, viewportMin - firstCharPosition);
03043                         m_TextComponent.rectTransform.anchoredPosition += new Vector2(offset, 0);
03044                         AssignPositioningIfNeeded();
03045                     }
03046                 }
03047                 else if (lastCharPosition < viewportMax && firstCharPosition < viewportMin)
03048                 {
03049                     float offset = Mathf.Min(viewportMax - lastCharPosition, viewportMin - firstCharPosition);
03050 
03051                     m_TextComponent.rectTransform.anchoredPosition += new Vector2(offset, 0);
03052                     AssignPositioningIfNeeded();
03053                 }
03054 
03055                 m_isLastKeyBackspace = false;
03056             }
03057 
03058             m_forceRectTransformAdjustment = false;
03059         }
03060 
03064         protected char Validate(string text, int pos, char ch)
03065         {
03066             // Validation is disabled
03067             if (characterValidation == CharacterValidation.None || !enabled)
03068                 return ch;
03069 
03070             if (characterValidation == CharacterValidation.Integer || characterValidation == CharacterValidation.Decimal)
03071             {
03072                 // Integer and decimal
03073                 bool cursorBeforeDash = (pos == 0 && text.Length > 0 && text[0] == '-');
03074                 bool selectionAtStart = stringPositionInternal == 0 || stringSelectPositionInternal == 0;
03075                 if (!cursorBeforeDash)
03076                 {
03077                     if (ch >= '0' && ch <= '9') return ch;
03078                     if (ch == '-' && (pos == 0 || selectionAtStart)) return ch;
03079                     if (ch == '.' && characterValidation == CharacterValidation.Decimal && !text.Contains(".")) return ch;
03080                 }
03081             }
03082             else if (characterValidation == CharacterValidation.Digit)
03083             {
03084                 if (ch >= '0' && ch <= '9') return ch;
03085             }
03086             else if (characterValidation == CharacterValidation.Alphanumeric)
03087             {
03088                 // All alphanumeric characters
03089                 if (ch >= 'A' && ch <= 'Z') return ch;
03090                 if (ch >= 'a' && ch <= 'z') return ch;
03091                 if (ch >= '0' && ch <= '9') return ch;
03092             }
03093             else if (characterValidation == CharacterValidation.Name)
03094             {
03095                 char lastChar = (text.Length > 0) ? text[Mathf.Clamp(pos, 0, text.Length - 1)] : ' ';
03096                 char nextChar = (text.Length > 0) ? text[Mathf.Clamp(pos + 1, 0, text.Length - 1)] : '\n';
03097 
03098                 if (char.IsLetter(ch))
03099                 {
03100                     // Space followed by a letter -- make sure it's capitalized
03101                     if (char.IsLower(ch) && lastChar == ' ')
03102                         return char.ToUpper(ch);
03103 
03104                     // Uppercase letters are only allowed after spaces (and apostrophes)
03105                     if (char.IsUpper(ch) && lastChar != ' ' && lastChar != '\'')
03106                         return char.ToLower(ch);
03107 
03108                     // If character was already in correct case, return it as-is.
03109                     // Also, letters that are neither upper nor lower case are always allowed.
03110                     return ch;
03111                 }
03112                 else if (ch == '\'')
03113                 {
03114                     // Don't allow more than one apostrophe
03115                     if (lastChar != ' ' && lastChar != '\'' && nextChar != '\'' && !text.Contains("'"))
03116                         return ch;
03117                 }
03118                 else if (ch == ' ')
03119                 {
03120                     // Don't allow more than one space in a row
03121                     if (lastChar != ' ' && lastChar != '\'' && nextChar != ' ' && nextChar != '\'')
03122                         return ch;
03123                 }
03124             }
03125             else if (characterValidation == CharacterValidation.EmailAddress)
03126             {
03127                 // From StackOverflow about allowed characters in email addresses:
03128                 // Uppercase and lowercase English letters (a-z, A-Z)
03129                 // Digits 0 to 9
03130                 // Characters ! # $ % & ' * + - / = ? ^ _ ` { | } ~
03131                 // Character . (dot, period, full stop) provided that it is not the first or last character,
03132                 // and provided also that it does not appear two or more times consecutively.
03133 
03134                 if (ch >= 'A' && ch <= 'Z') return ch;
03135                 if (ch >= 'a' && ch <= 'z') return ch;
03136                 if (ch >= '0' && ch <= '9') return ch;
03137                 if (ch == '@' && text.IndexOf('@') == -1) return ch;
03138                 if (kEmailSpecialCharacters.IndexOf(ch) != -1) return ch;
03139                 if (ch == '.')
03140                 {
03141                     char lastChar = (text.Length > 0) ? text[Mathf.Clamp(pos, 0, text.Length - 1)] : ' ';
03142                     char nextChar = (text.Length > 0) ? text[Mathf.Clamp(pos + 1, 0, text.Length - 1)] : '\n';
03143                     if (lastChar != '.' && nextChar != '.')
03144                         return ch;
03145                 }
03146             }
03147             else if (characterValidation == CharacterValidation.Regex)
03148             {
03149                 // Regex expression
03150                 if (Regex.IsMatch(ch.ToString(), m_RegexValue))
03151                 {
03152                     return ch;
03153                 }
03154             }
03155             else if (characterValidation == CharacterValidation.CustomValidator)
03156             {
03157                 if (m_InputValidator != null)
03158                 {
03159                     char c = m_InputValidator.Validate(ref text, ref pos, ch);
03160                     m_Text = text;
03161                     stringSelectPositionInternal = stringPositionInternal = pos;
03162                     return c;
03163                 }
03164             }
03165             return (char)0;
03166         }
03167 
03168         public void ActivateInputField()
03169         {
03170             if (m_TextComponent == null || m_TextComponent.font == null || !IsActive() || !IsInteractable())
03171                 return;
03172 
03173             if (isFocused)
03174             {
03175                 if (m_Keyboard != null && !m_Keyboard.active)
03176                 {
03177                     m_Keyboard.active = true;
03178                     m_Keyboard.text = m_Text;
03179                 }
03180             }
03181 
03182             m_ShouldActivateNextUpdate = true;
03183         }
03184 
03185         private void ActivateInputFieldInternal()
03186         {
03187             if (EventSystem.current == null)
03188                 return;
03189 
03190             if (EventSystem.current.currentSelectedGameObject != gameObject)
03191                 EventSystem.current.SetSelectedGameObject(gameObject);
03192 
03193             if (TouchScreenKeyboard.isSupported)
03194             {
03195                 if (Input.touchSupported)
03196                 {
03197                     TouchScreenKeyboard.hideInput = shouldHideMobileInput;
03198                 }
03199 
03200                 m_Keyboard = (inputType == InputType.Password) ?
03201                     TouchScreenKeyboard.Open(m_Text, keyboardType, false, multiLine, true) :
03202                     TouchScreenKeyboard.Open(m_Text, keyboardType, inputType == InputType.AutoCorrect, multiLine);
03203 
03204                 // Mimics OnFocus but as mobile doesn't properly support select all
03205                 // just set it to the end of the text (where it would move when typing starts)
03206                 MoveTextEnd(false);
03207             }
03208             else
03209             {
03210                 Input.imeCompositionMode = IMECompositionMode.On;
03211                 OnFocus();
03212             }
03213 
03214             //m_StringPosition = m_StringSelectPosition = 0;
03215             //m_CaretPosition = m_CaretSelectPosition = 0;
03216 
03217             m_AllowInput = true;
03218             m_OriginalText = text;
03219             m_WasCanceled = false;
03220             SetCaretVisible();
03221             UpdateLabel();
03222         }
03223 
03224         public override void OnSelect(BaseEventData eventData)
03225         {
03226             //Debug.Log("OnSelect()");
03227 
03228             base.OnSelect(eventData);
03229             SendOnFocus();
03230 
03231             ActivateInputField();
03232         }
