03247         public void DeactivateInputField()
03248         {
03249             //Debug.Log("Deactivate Input Field...");
03250 
03251             // Not activated do nothing.
03252             if (!m_AllowInput)
03253                 return;
03254 
03255             m_HasDoneFocusTransition = false;
03256             m_AllowInput = false;
03257 
03258             if (m_Placeholder != null)
03259                 m_Placeholder.enabled = string.IsNullOrEmpty(m_Text);
03260 
03261             if (m_TextComponent != null && IsInteractable())
03262             {
03263                 if (m_WasCanceled && m_RestoreOriginalTextOnEscape)
03264                     text = m_OriginalText;
03265 
03266                 if (m_Keyboard != null)
03267                 {
03268                     m_Keyboard.active = false;
03269                     m_Keyboard = null;
03270                 }
03271 
03272                 if (m_ResetOnDeActivation)
03273                 {
03274                     m_StringPosition = m_StringSelectPosition = 0;
03275                     m_CaretPosition = m_CaretSelectPosition = 0;
03276                     m_TextComponent.rectTransform.localPosition = m_DefaultTransformPosition;
03277 
03278                     if (caretRectTrans != null)
03279                         caretRectTrans.localPosition = Vector3.zero;
03280 
03281                     //m_ForceDeactivation = false;
03282                 }
03283 
03284                 SendOnEndEdit();
03285                 SendOnEndTextSelection();
03286 
03287                 Input.imeCompositionMode = IMECompositionMode.Auto;
03288             }
03289 
03290             MarkGeometryAsDirty();
03291 
03292             // Scrollbar should be updated.
03293             m_IsScrollbarUpdateRequired = true;
03294         }
03295 
03296         public override void OnDeselect(BaseEventData eventData)
03297         {
03298             //return;
03299 
03300             DeactivateInputField();
03301 
03302             base.OnDeselect(eventData);
03303             SendOnFocusLost();
03304         }
