03325         private void EnforceContentType()
03326         {
03327             switch (contentType)
03328             {
03329                 case ContentType.Standard:
03330                     {
03331                         // Don't enforce line type for this content type.
03332                         m_InputType = InputType.Standard;
03333                         m_KeyboardType = TouchScreenKeyboardType.Default;
03334                         m_CharacterValidation = CharacterValidation.None;
03335                         return;
03336                     }
03337                 case ContentType.Autocorrected:
03338                     {
03339                         // Don't enforce line type for this content type.
03340                         m_InputType = InputType.AutoCorrect;
03341                         m_KeyboardType = TouchScreenKeyboardType.Default;
03342                         m_CharacterValidation = CharacterValidation.None;
03343                         return;
03344                     }
03345                 case ContentType.IntegerNumber:
03346                     {
03347                         m_LineType = LineType.SingleLine;
03348                         m_TextComponent.enableWordWrapping = false;
03349                         m_InputType = InputType.Standard;
03350                         m_KeyboardType = TouchScreenKeyboardType.NumberPad;
03351                         m_CharacterValidation = CharacterValidation.Integer;
03352                         return;
03353                     }
03354                 case ContentType.DecimalNumber:
03355                     {
03356                         m_LineType = LineType.SingleLine;
03357                         m_TextComponent.enableWordWrapping = false;
03358                         m_InputType = InputType.Standard;
03359                         m_KeyboardType = TouchScreenKeyboardType.NumbersAndPunctuation;
03360                         m_CharacterValidation = CharacterValidation.Decimal;
03361                         return;
03362                     }
03363                 case ContentType.Alphanumeric:
03364                     {
03365                         m_LineType = LineType.SingleLine;
03366                         m_TextComponent.enableWordWrapping = false;
03367                         m_InputType = InputType.Standard;
03368                         m_KeyboardType = TouchScreenKeyboardType.ASCIICapable;
03369                         m_CharacterValidation = CharacterValidation.Alphanumeric;
03370                         return;
03371                     }
03372                 case ContentType.Name:
03373                     {
03374                         m_LineType = LineType.SingleLine;
03375                         m_TextComponent.enableWordWrapping = false;
03376                         m_InputType = InputType.Standard;
03377                         m_KeyboardType = TouchScreenKeyboardType.Default;
03378                         m_CharacterValidation = CharacterValidation.Name;
03379                         return;
03380                     }
03381                 case ContentType.EmailAddress:
03382                     {
03383                         m_LineType = LineType.SingleLine;
03384                         m_TextComponent.enableWordWrapping = false;
03385                         m_InputType = InputType.Standard;
03386                         m_KeyboardType = TouchScreenKeyboardType.EmailAddress;
03387                         m_CharacterValidation = CharacterValidation.EmailAddress;
03388                         return;
03389                     }
03390                 case ContentType.Password:
03391                     {
03392                         m_LineType = LineType.SingleLine;
03393                         m_TextComponent.enableWordWrapping = false;
03394                         m_InputType = InputType.Password;
03395                         m_KeyboardType = TouchScreenKeyboardType.Default;
03396                         m_CharacterValidation = CharacterValidation.None;
03397                         return;
03398                     }
03399                 case ContentType.Pin:
03400                     {
03401                         m_LineType = LineType.SingleLine;
03402                         m_TextComponent.enableWordWrapping = false;
03403                         m_InputType = InputType.Password;
03404                         m_KeyboardType = TouchScreenKeyboardType.NumberPad;
03405                         m_CharacterValidation = CharacterValidation.Digit;
03406                         return;
03407                     }
03408                 default:
03409                     {
03410                         // Includes Custom type. Nothing should be enforced.
03411                         return;
03412                     }
03413             }
03414         }
