03417         void SetTextComponentWrapMode()
03418         {
03419             if (m_TextComponent == null)
03420                 return;
03421 
03422             if (m_LineType == LineType.SingleLine)
03423                 m_TextComponent.enableWordWrapping = false;
03424             else
03425                 m_TextComponent.enableWordWrapping = true;
03426         }
