03437         void SetToCustomIfContentTypeIsNot(params ContentType[] allowedContentTypes)
03438         {
03439             if (contentType == ContentType.Custom)
03440                 return;
03441 
03442             for (int i = 0; i < allowedContentTypes.Length; i++)
03443                 if (contentType == allowedContentTypes[i])
03444                     return;
03445 
03446             contentType = ContentType.Custom;
03447         }
03448 
03449         void SetToCustom()
03450         {
03451             if (contentType == ContentType.Custom)
03452                 return;
03453 
03454             contentType = ContentType.Custom;
03455         }
03456 
03457         void SetToCustom(CharacterValidation characterValidation)
03458         {
03459             if (contentType == ContentType.Custom)
03460             {
03461                 characterValidation = CharacterValidation.CustomValidator;
03462                 return;
03463             }
03464 
03465             contentType = ContentType.Custom;
03466             characterValidation = CharacterValidation.CustomValidator;
03467         }
