03470         protected override void DoStateTransition(SelectionState state, bool instant)
03471         {
03472             if (m_HasDoneFocusTransition)
03473                 state = SelectionState.Highlighted;
03474             else if (state == SelectionState.Pressed)
03475                 m_HasDoneFocusTransition = true;
03476 
03477             base.DoStateTransition(state, instant);
03478         }
